css:
/* i really want this to be global */
.logo path {
	display:none;
}
.navBar-header {
    background: url(https://i.imgur.com/3URjiM8.png);
    background-repeat: no-repeat;
}
.progress-bar__fg {
    background-color: #FFFFFF ;
    border-radius: 2px;
}
._2rScaV3bBHoj7O47a_JnaH, .Vg0L1vwJXv5JSYn-HUx0C:hover {
    color: #FFFFFF;
}
.progress-bar:hover .progress-bar__fg {
    background-color: #ffffff;
}
.control-button {
    background-color: transparent;
    border: none;
    color: #ffffff;
    cursor: pointer;
    width: 32px;
    min-width: 32px;
    height: 32px;
}
.control-button--circled:after {
    content: "";
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    border-radius: 500px;
}
.control-button:hover {
    color: #ffffff;
}
._2rScaV3bBHoj7O47a_JnaH:after, ._2rScaV3bBHoj7O47a_JnaH:hover:after {
    background-color: #00aae9;
}
.navlist-itemlink.is-active {
    color: #FFFFFF;
}
.btn.btn-green:focus, .btn.btn-green:hover {
    background-color: #00aae9;
}
.btn.btn-green {
    background-color: #00aae9;
    padding: 13px 44px;
}
.inputBox-input {
    box-sizing: border-box;
    width: 100%;
    background-color: transparent;
    border: none;
    color: #00aae9;
    font-weight: 700;
    font-size: 62px;
    line-height: 1.2;
    text-shadow: 0 0 0 #fff;
    -webkit-text-fill-color: transparent;
    -moz-text-fill-color: transparent;
    -ms-text-fill-color: transparent;
}
._1YMukI_FrE7QRuUVSvq_Qe {
    background-image: url(https://staticdelivery.nexusmods.com/mods/1151/images/19127-1-1476046523.jpg) !important;
}
.now-playing-bar-container {
    min-width: 620px;
    background-color: #080808;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -ms-flex-direction: column;
    flex-direction: column;
    height: auto;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}
.inputBox {
    position: relative;
    background-color: #080808;
    padding: 1.5em 2em;
}
.navBar {
    padding: 24px 24px 95px;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -ms-flex-direction: column;
    flex-direction: column;
    height: 100%;
    -webkit-user-select: none;
    background: #080808;
    opacity:.9;
}
.cover-art .cover-art-playback:hover {
    border-color: #00aae9;
    -webkit-transform: scale(1.2);
    transform: scale(1.2);
}
.cover-art .cover-art-playback {
    background-color: transparent;
    display: none;
    position: absolute;
    z-index: 3;
    width: 62px;
    height: 62px;
    margin-left: -31px;
    margin-top: -31px;
    top: 50%;
    left: 50%;
    color: #00aae9;
}
.control-button--circled:after {
    content: "";
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    border-radius: 500px;
    box-shadow: none;
}
.connect-device-list-item--active {
    color: #00aae9;
}
.connect-device-list-item--active .connect-device-list-item__device-subtitle {
    color: #00aae9;
}
.connect-device-list-item--active .connect-device-list-item__icon {
    color: #00aae9;
}
.control-button--circled:hover:after {
    box-shadow:none;
}
