css:
@-moz-document domain("roblox.com") {
.content {
    max-width: 1338px;
    background-color: #36393f !important;
    padding-top: 12px;
    margin: 0 auto;
}

.container-fluid {
    margin-right: auto;
    margin-left: auto;
    padding-left: 0;
    padding-right: 0;
    background-color: rgba(78, 77, 62, .83) !important
}
.section-content {
    background-color: #24262be3;
    padding: 15px;
    position: relative;
}
.friend-list .friend .friend-link,
.people-list .friend .friend-link {
    display: block;
    position: relative;
    margin: 0 auto;
    color: #ffffff;
}
body {
    background-color: #fff;
    color: #000;
    font-family: "Source Sans Pro", Arial, Helvetica, sans-serif;
    font-weight: 300;
    font-size: 16px;
}
.container-footer {
    background-color: #4b4e54 !important;
}
.bg-gray-4 {
    background-color: #36393f !important;
}
.scroller {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    background-color: #4a4d54;
    border: 1px solid #b8b8b8;
    width: 30px;
    height: 223px;
    zoom: 1;
    position: absolute;
    z-index: 1000;
    top: 0;
}
body {
    background-color: #36393f !important;
    color: #36393f !important;
    font-family: "Source Sans Pro", Arial, Helvetica, sans-serif;
    font-weight: 300;
    font-size: 16px;
}
body {
    background-color: #36393f !important;
    color: #5178e6 !important;
    font-family: "Source Sans Pro", Arial, Helvetica, sans-serif;
    font-weight: 300;
    font-size: 16px;
}
.notification-red {
    background-color: #7d7a7a !important;
    border-radius: 12px;
    color: #fff;
    font-size: 12px;
    font-weight: 400;
    padding: 1px 6px;
    min-width: 18px;
    text-align: center;
}
.text,
.text-nav {
    color: #fff !important;
}
.stack .stack-list .stack-row {
    background-color: #24262be3 !important;
}
.game-card-container {
    background-color: #24262be3 !important;
    position: relative;
    border-radius: 3px;
    float: left;
    height: 100%;
    margin: 0 auto;
    padding: 0;
    text-align: left;
    width: 100%;
}
.rbx-left-col {
    background-color: #36393f24 !important;
}
.menu-vertical {
    background-color: #2d2c2c !important;
}
#Body {
    min-height: 550px;
    padding: 5px 0 25px 0;
    margin: 0 auto;
    max-width: 970px;
    background-color: #36393f;
}
.redesign .tab_white_31h_container .ajax__tab_active .ajax__tab_inner,
.tab.active,
.tab-container > .tab-active,
.tab-container > .tab-active:hover {
    background-color: #36393f;
    margin-top: 0;
    padding: 9px 7px;
    border-bottom: 0;
    position: relative;
    border-color: #ccc;
    z-index: 1;
    margin: 0 1px 0 0;
    top: 1px;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
}
.redesign .tab_white_31h_container .ajax__tab_inner,
.tab,
.tab-container > div {
    float: left;
    background-color: #d6d6d600;
    padding: 7px;
    border: 1px solid #9e9e9e;
    font-weight: bold;
    font-size: 15px;
    margin: 4px 2px 0 1px;
    border-bottom-width: 0;
    position: relative;
    top: -1px;
}
#build-page .gear-button-wrapper {
    top: 5px;
    width: 38px;
    background-color: #36393f !important;
    border-left: 1px solid;
    border-right: 1px solid;
    border-color: #ffffff00 !important;
    height: 37px;
    position: relative;
    margin-right: 5px;
}
.divider-left {
    border-left: 1px solid #ccc;
    border-left-width: 1px;
    border-left-style: solid;
    border-left-color: rgb(21, 22, 25) !important;
}
h2,
h2 a:link,
h2 a:visited,
h2 a:active,
h2 .text {
    font-size: 30px;
    font-weight: normal;
    color: #5178e6;
    letter-spacing: -1px;
    text-decoration: none;
}
body,
.text,
pre {
    font-family: 'Source Sans Pro', Arial, Helvetica, sans-serif;
    color: #5178e6;
    font-size: 14px;
    line-height: 1.428;
}
#build-page td.menu-area a.tab-item {
    display: block;
    padding: 5px 10px;
    color: #5178e6!important;
    font-size: 16px;
}
.chat-main .chat-header {
    background-color: #707891;
}
.chat-windows-header {
    background-color: #000 !important;
    color: #fff;
}
h1, h1 a, h1 a:visited, h1 a:active, h1 a:link {
    font-size: 32px;
    font-weight: bold;
    margin: 12px;
    color: #5178e6 !important;
    letter-spacing: -1px;
}
.product-summary .product-description {
    text-align: left;
    float: left;
    color: #5178e6;
}
.product-summary {
    color: #5178e6;
    text-align: center;
}
.dialog-container .dialog-header {
    background-color: #8e555500;
    color: #fff;
}
.dialog-container .dialog-message-container .dialog-message {
    background-color: #1B2A35;
    border: 1px solid #00a2ff;
    color: #fff;
}
}
