css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://friendfeed.com/") {
.entry { direction:rtl; }
.entry .profile { float:right!important; }
.entry .body { margin-left:0!important; margin-right:63px!important; }
.entry.nopicture .body { margin-right:0!important; margin-left:0!important; }
.entry .likes, .entry .expandcomment, .entry .commentform, .entry .comment.deleted { padding-left:0!important; padding-right:21px!important; }
.entry .likes { background:transparent url(http://beta.friendfeed.com/static/images/n-smile.png) no-repeat scroll right top!important; }
.entry .comment, .entry .commentform { background:transparent url(http://beta.friendfeed.com/static/images/comment-lighter.png?v=f43f) no-repeat scroll 100% 1px!important; }
.entry .comment .content { margin-left:0!important; margin-right:21px!important; }
.entry.private .info { background-image:url(http://beta.friendfeed.com/static/images/lock.png?v=122c)!important; padding-left:0!important; padding-right: 14px!important; }
.entry.private .info, .entry.direct .info { background-position:right center!important; padding-left:0!important; padding-right:16px!important; }
.entry .comment.deleted, .entry .comment.friend, .entry .comment.owner, .entry .commentform { background-image:url(http://beta.friendfeed.com/static/images/n-comment.png?v=a22a)!important; }
.entry .info, .entry .likes, .entry .expandcomment { direction:ltr; text-align:right; }
.sharebox .title textarea { direction:rtl!important; }
body {font-family:tahoma !important;}
body,input,textarea,td,select,option{font-family:tahoma !important;font-size:11px!important;}
body.fbiframe,.fbiframe input,.fbiframe textarea,.fbiframe td,.fbiframe select,.fbiframe option{font-family:tahoma !important;font-size:11px!important;}
}
