css:
@-moz-document domain("developer.android.com") {
body {
    color: #000;
}
.card-info .description .text {
    color: #000;
}

.devsite-landing-row-item-description-content, .devsite-article {
    color: #000;
}


#jd-content .absent a {
    color: #000 !important;
    border-bottom: 1px dashed #555;
}
#jd-content .absent a:hover {
    cursor: pointer !important;
    
}
#jd-content .absent p {
    color: #444 !important;
}

/*******************/

.devsite-top-section {
    height: 0 !important;
    overflow: hidden;
    border-bottom: 5px solid #039be5; 
}
                                            
.devsite-top-section:hover {
	height: auto !important;
}

.devsite-page-nav.devsite-nav {
    position: fixed !important;
    right: 0 !important;
    left: 99% !important;
    top: 5px !important;
    overflow: hidden;
    width: 0 !important;
    height: 200px;
}
.devsite-page-nav.devsite-nav:hover {
    width: auto !important;
    left: 90% !important;
    height: inherit !important;
    overflow: auto;
}


.devsite-section-nav {
    width: 0px !important;
    overflow: hidden;
    border-left: 5px solid #039be5;
    z-index: 4444;
    position: fixed !important;
    top: 5px !important;
}

.devsite-section-nav:hover {
    width: auto !important;
    overflow: auto;
}

.devsite-article {
    box-sizing: border-box;
	margin-left: 40px !important;
	position: relative;
	width: auto !important;
}



}
