css:
@-moz-document domain("laravel.com") {
body {
    color: #000;
}

.docs article p {
    color: #000;
    font-size: 1rem;
    font-family: Roboto;
    line-height: 1.5;
}

.docs article code[class*=language-], .docs article pre[class*=language-] {
    line-height: normal;
    font-size: 13px;
}

.docs article {
	padding: 25px 50px 50px 0px;
	margin-left: 260px;
}

.docs article h3 {
	font-weight: 600;
	font-size: 26px;
}

.docs article h4 {
	font-size: 20px;
	margin-top: 40px;
	margin-bottom: 10px;
}

  :not(pre) > code[class*="language-"],
  code[class^=" language"],
  pre[class^=" language"],
  .browser-window .window-content pre[class*="language-"]
  {
    background: #263238;
    color: #C3D3DE;
	font-size: 1rem;
  }


  /* Laravel */

  .token.keyword
  {
    color: #C792EA;
  }

  .token.package
  {
    color: #C3D3DE;
  }
  
  .token.delimiter
  { 
    color: #C792EA;
  }

  .token.class-name
  { 
    color: #F78C6C;
  }
  
  .token.function
  {
    color: #82AAFF;
  }

  .token.variable
  {
    color: #EEFFE3;
  }

  .token.punctuation
  {
    color: #89DDFF;
  }

  .token.operator
  {
    color: #89DDFF;
  }

  .token.scope
  {
    color: #FFCB6B;
  }

  .token.string
  {
    color: #C3E88D;
  }

  .token.comment
  {
    color: #546E7A;
  }

  .token.number
  {
    color: #F78C6C;
  }

  .token.property
  {
    color: #EEFFFF;
  }

  .token.constant
  { 
    color: #F78C6C;
  }

  /* Blade */

  .token.tag
  {
    color: #F07171;
  }

  .token.attr-name
  {
    color: #FFCB6B;
  }

  .token.attr-value
  {
    color: #C3E88D;
  }


  code[class*="language-"], pre[class*="language-"]
  {
    text-shadow: none;
  }

  footer.main
  {
    background-color: #303030;
  }

  footer.main > ul > li > a
  {
    color: #f0f0f0;
  }
}
