css:
@-moz-document domain("rubenalamina.mx") {
body.custom-background,
#wrapper,
.page-title h1,
.page-title h2,
h1,
h2,
h3,
h4,
h5,
h6,
#navigation,
.menu > li > a,
.menu ul a,
table.table-division td.cell-high,
#footer,
#footer p,
#footer a,
#footer-bottom,
table.table-division th,
b,
strong,
table.table-division td.cell-brand {
    background: #101010;
    color: #888;
}

input[type="text"],
input[type="password"],
input[type="tel"],
input[type="email"],
textarea,
.menu > li:hover > a,
.menu ul a:hover,
table.table-division tr:hover td {
    background: #2a2a2a;
}

#content div.sharedaddy,
#main div.sharedaddy,
div.sharedaddy {
    visibility: hidden;
}

table.table-division td {
    background: #101010 !important;
}

table.table-division th.header-he {
    color: #888;
}
}
