css:
/* i really want this to be global */
html {
    font: 15px / 1.4 sans-serif;
    font-family:'Nunito', 'Helvetica Neue', Helvetica, Roboto, Oxygen, Ubuntu, Cantarell, 'Fira Sans', 'Droid Sans', sans-serif;
    font-weight: 400;
    color: #2980b9;
    margin: 0;
    padding: 0;
    word-wrap: break-word;
    background-color: #ecf0f1;
}
.p-body-inner {
    background: #2c3e50;
    padding: 15px;
    box-shadow: 0px 0px 6px rgba(0, 0, 0, 0.03);
}
.p-breadcrumbs--container {
    background: #2c3e50;
    padding: 10px;
    border-radius: 2px;
    margin-bottom: 15px;
}
.p-breadcrumbs>li a {
    display: inline-block;
    vertical-align: bottom;
    max-width: 300px;
    color: #2980b9;
    overflow: hidden;
    white-space: nowrap;
    word-wrap: normal;
    text-overflow: ellipsis;
}
.p-body-header {
    padding-bottom: 15px;
    border-bottom: 1px dashed #2980b9;
    margin-bottom: 15px;
}
.p-pageWrapper {
    position: relative;
    display: flex;
    flex-direction: column;
    min-height: 100vh;
    background: #42474a;
}
.header--proxy {
    position: relative;
    height: 550px;
    background: transparent url(https://i.imgur.com/TiiCw8d.png) no-repeat center / cover;
    background-attachment: fixed;
    margin-top: -170px;
    z-index: 2;
}
.header-proxy--overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: #42474a;
    opacity: 0.95;
    z-index: 2;
}
.p-navSticky.is-sticky {
    z-index: 400;
    background: #2c3e50;
    box-shadow: 0 0 8px 3px rgba(0, 0, 0, 0.12);
}
.p-guest--message {
    color: #48adde;
    padding-top: 0;
    padding-right: 20px;
    padding-bottom: 0;
    padding-left: 20px;
    position: absolute;
    top: 50%;
    left: 0;
    width: 100%;
    text-align: center;
    text-shadow: 0 2px 4px rgba(0, 0, 0, 0.2);
    z-index: 3;
}
.p-header {
    color: #2b6cb6;
    position: relative;
    z-index: 3;
}
.p-sectionLinks-inner {
    max-width: 1300px;
    padding: 0 10px;
    margin: 0 auto;
    font-size: 13px;
    color: #48adde;
    margin-top: -20px;
    padding-left: 0px;
    padding-right: 0px;
}
.p-nav-list .p-navEl {
    font-size: 13px;
    color: #48adde;
    font-weight: 700;
    text-decoration: none;
    text-transform: uppercase;
    opacity: 0.6;
    border: 2px solid transparent;
    border-radius: 20px;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
}
.p-nav-list .p-navEl.is-selected {
    color: #48adde;
    border-color: #2c3e50;
    opacity: 1;
}
.node {
    background: #2c3e50;
}
.block--category .block-body {
    background: #2c3e50;
    padding: 7px;
}
a {
    color: #48adde;
    text-decoration: none;
}
.block-container {
    color: #48adde;
    background: #2c3e50;
    border: 1px solid #2980b9;
    border-radius: 3px;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
    -webkit-transition-property: border margin;
    transition-property: border margin;
}
.block-header {
    padding: 6px 10px;
    margin: 0;
    font-weight: 400;
    text-decoration: none;
    font-size: 17px;
    color: #414548;
    background: #48adde;
}
.p-body-sidebar .block-container .block-minorHeader {
    background: #48adde;
    color: #414548;
    padding: 10px;
    margin-bottom: 5px;
}
.p-body-sidebar .block-container {
    background: #2c3e50;
    border: none;
    overflow: hidden;
}
.block-footer {
    padding: 6px 10px;
    font-size: 12px;
    color: #2980b9;
    background: #2c3e50;
    border-top: 1px solid #2980b9;
}
.p-body-sidebar .block-container .block-row {
    border-bottom: 1px solid #2980b9;
}
.node+.node {
    border-top: 1px solid #2980b9;
    margin-top: 5px;
}
.p-staffBar {
    font-size: 13px;
    color: #48adde;
    background: #2c3e50;
}
.pageNav-page {
    display: table-cell;
    background: #48adde;
    color: #fff;
    border: 1px solid #48adde;
    font-size: 13px;
    white-space: nowrap;
}
.pageNav-jump {
    display: inline-block;
    background: #48adde;
    color: #fff;
    border: 1px solid #48adde;
    font-size: 13px;
    white-space: nowrap;
    border-radius: 2px;
    padding: 8px 8px;
}
.pre-footer--column h3 {
    color: #48adde;
    font-weight: 700;
    padding-top: 15px;
    padding-right: 0;
    padding-bottom: 15px;
    padding-left: 0;
    text-transform: uppercase;
    margin: 0;
}
.tabs--standalone {
    padding: 0;
    margin-bottom: 20px;
    font-weight: 400;
    border: 1px solid #48adde;
    font-size: 15px;
    color: #2c3e50;
    background: #48adde;
    font-size: 0;
}
.tabs--standalone .tabs-tab.is-active {
    color: #2c3e50;
    border-color: #2980b9;
}
.button.button--cta, a.button.button--cta {
    color: #48adde;
    background: #2b6cb6;
}
.button, a.button {
    display: inline-block;
    text-decoration: none;
    cursor: pointer;
    white-space: nowrap;
    -webkit-transition: background-color .25s ease;
    transition: background-color .25s ease;
    font-size: 13px;
    border-radius: 20px;
    padding-top: 8px;
    padding-right: 12px;
    padding-bottom: 8px;
    padding-left: 12px;
    text-align: center;
    border: none;
    color: #48adde;
    background: #2b6cb6;
}
.button.button--primary, a.button.button--primary {
    color: #48adde;
    background: #2b6cb6;
    min-width: 80px;
}
.input {
    font-size: 15px;
    color: #42474a;
    background: #48adde;
    border: 1px solid #48adde;
    border-radius: 4px;
    padding: 6px;
    display: block;
    width: 100%;
    line-height: 1.4;
    text-align: left;
    word-wrap: break-word;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
}
.p-footer {
    font-size: 13px;
    color: #48adde;
    background: #1d1f25;
    padding-top: 20px;
}
.p-footer a {
    color: #42474a;
}
html::-webkit-scrollbar-thumb {
    background: #2c3e50;
}
.menu-linkRow {
    display: block;
    padding: 6px 15px 6px 12px;
    color: #48adde;
    text-decoration: none;
}
.menu-content {
    margin: 0;
    padding: 0;
    list-style: none;
    text-align: left;
    font-size: 13px;
    color: #48adde;
    background: #2c3e50;
    border-radius: 3px;
    border-top: 3px solid #3cc;
}
.block-filterBar {
    padding: 10px;
    font-size: 13px;
    color: #2c3e50;
    background: #48adde;
}
.block-filterBar .filterBar-filterToggle {
    background: #48adde;
}
.structItem-minor {
    font-size: 12px;
    color: #48adde;
}
.structItem {
    display: table;
    table-layout: fixed;
    border-collapse: collapse;
    border-top: 1px solid #2c3e50;
    list-style: none;
    margin: 0;
    padding: 0;
    width: 100%;
}
.structItemContainer {
    background: #2c3e50;
    padding: 7px;
}
.structItem {
    background: #2c3e50;
    margin-bottom: 5px;
}
.structItem {
    background: #2c3e50;
    margin-bottom: 5px;
}
.block-textHeader {
    margin: 3px 0;
    padding: 0;
    font-weight: 400;
    font-size: 17px;
    color: #48adde;
}
.memberOverviewBlock-seeMore {
    background: #2c3e50;
    border: 1px solid #2c3e50;
    padding: 3px;
    text-align: center;
    margin-top: auto;
}
.message-cell.message-cell--user, .message-cell.message-cell--action {
    position: relative;
    background: #2c3e50;
    border-right: 1px solid #2c3e50;
    min-width: 0;
}
.message-responseRow {
    margin-top: -1px;
    background: #2c3e50;
    border: 1px solid #2c3e50;
    padding: 6px;
}
.reactions-bar .reactions-left .th_reactions__listItem .reaction-count {
    background: #2c3e50;
    border-left: 1px solid #2c3e50;
}
.reactions-bar .reactions-left .th_reactions__listItem {
    margin-right: 5px;
    border: 1px solid #2c3e50;
    border-radius: 2px;
    display: flex;
    align-items: stretch;
    overflow: hidden;
    line-height: 22px;
}
.th_reactions__trigger {
    cursor: pointer;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    font-size: 20px;
    color: #8c8c8c;
    background: #2c3e50;
    border: 1px solid #2c3e50;
    border-radius: 100%;
    padding: 5px;
    text-align: center;
    flex: 0 0 auto;
    line-height: 22px;
    display: flex;
    align-items: stretch;
}
:not(.reactions-bar--none) .th_reactions__scrollContainer, .reactions-bar--modal .th_reactions__scrollContainer {
    position: absolute;
    transition: opacity .2s;
    background: #2c3e50;
    border: 1px solid #2c3e50;
    box-shadow: 0 3px 6px rgba(0, 0, 0, 0.16), 0 3px 6px rgba(0, 0, 0, 0.23);
}
.structItem-minor a {
    color: #48adde;
    text-decoration: none;
}
.is-unread .structItem-title {
    color: #2c3e50;
    font-size: 15px;
    font-weight: 400;
    margin: 0;
    padding: 0;
}
.block-tabHeader {
    padding: 0;
    margin: 0;
    font-weight: 400;
    font-size: 15px;
    color: #5b5b5b;
    background: #48adde;
    border-bottom: 1px solid #48adde;
    font-size: 0;
}
.block-tabHeader .tabs-tab.is-active {
    background: none;
    color: #2c3e50;
    border-color: #3cc;
}
.block-row.block-row--separated+.block-row {
    border-top: 1px solid #48adde;
}
.contentRow-minor {
    font-size: 13px;
    color: #48adde;
}
.menu-separator {
    margin: 0 7.5px;
    padding: 0;
    border: none;
    border-top: 1px solid #48adde;
}
.menu-footer {
    padding: 6px 15px;
    font-size: 12px;
    color: #838383;
    background: #2c3e50;
    border-top: 1px solid #2c3e50;
}
.menu--account .menu-row.menu-row--highlighted {
    background: #2c3e50;
    color: #48adde;
}
.menu--account .menu-row.menu-row--highlighted .username {
    color: #48adde;
}
.pairs>dt {
    padding: 0;
    margin: 0;
    color: #48adde;
}
.memberHeader-main {
    color: #48adde;
    background: #2c3e50;
}
.button.button--link, a.button.button--link {
    background: #48adde;
    color: #2c3e50;
    border: 1px solid #48adde;
}
.memberHeader-separator {
    margin: 10px 0;
    border: none;
    border-top: 1px solid #48adde;
}
.block--messages .block-container {
    background: #2c3e50;
    padding: 7px;
    border: none;
    border-radius: 0;
}
.block--messages .message, .block--messages .block-row {
    color: #48adde;
    background: #2c3e50;
}
.block--messages .block-container {
    background: #2c3e50;
    padding: 7px;
    border: 1px solid #2980b9;
    border-radius: 1px;
}
.comment-actionBar .actionBar-set {
    margin-top: 6px;
    color: #48adde;
}
.mcShowcaseBlock {
    background-color: #2c3e50;
    padding: 4px 2px;
    height: 42px;
    margin-top: 10px;
}
.mcShowcaseBlock .playerData .playerUsername {
    color: #48adde;
    overflow-y: hidden;
    height: 13px;
    margin-bottom: 2px;
}
.ncms-group-badge {
    font-size: 11px;
    background-color: #cecece;
    color: #2c3e50;
    padding: 1px 2px;
    display: inline-block;
    text-transform: uppercase;
}
.message-cell.message-cell--user, .message-cell.message-cell--action {
    position: relative;
    background: #2c3e50;
    border: 1px solid #48adde;
    min-width: 0;
}
.bbCodeBlock-title {
    padding: 6px 10px;
    font-size: 13px;
    color: #42474a;
    background: #48adde;
}
.bbCodeBlock {
    margin: .75em 0;
    background: #2c3e50;
    border: 1px solid #48adde;
    border-left: 3px solid #3cc;
}
.message-attribution {
    color: #48adde;
    font-size: 12px;
    padding-bottom: 3px;
    border-bottom: 1px solid #48adde;
}
.fr-box.fr-basic .fr-element {
    color: #48adde;
    padding: 10px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    overflow-x: auto;
    min-height: 40px;
}
.fr-wrapper .fr-placeholder {
    position: absolute;
    font-size: 12px;
    color: #48adde;
    z-index: 1;
    display: none;
    top: 0;
    left: 0;
    right: 0;
    overflow: hidden;
}
.fr-box.fr-basic .fr-wrapper {
    background: #42474a;
    border: 1px solid #48adde;
    border-top: 0;
    top: 0;
    left: 0;
}
.fr-toolbar {
    color: #5b5b5b;
    background: #48adde;
    position: relative;
    z-index: 4;
    font-family:'Nunito', 'Helvetica Neue', Helvetica, Roboto, Oxygen, Ubuntu, Cantarell, 'Fira Sans', 'Droid Sans', sans-serif;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    user-select: none;
    -o-user-select: none;
    -moz-user-select: none;
    -khtml-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
    padding: 0 1px;
    border-radius: 2px;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    text-align: left;
    border: 1px solid #48adde;
    border-top: 3px solid #3cc;
    text-rendering: optimizelegibility;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    line-height: 1.2;
}
.fr-toolbar .fr-command.fr-btn, .fr-popup .fr-command.fr-btn {
    background: transparent;
    color: #42474a;
    -moz-outline: 0;
    outline: 0;
    border: 0;
    line-height: 1;
    cursor: pointer;
    text-align: left;
    margin: 0px 1px;
    -webkit-transition: background .25s ease 0s;
    -moz-transition: background .25s ease 0s;
    -ms-transition: background .25s ease 0s;
    -o-transition: background .25s ease 0s;
    border-radius: 0;
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    z-index: 2;
    position: relative;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    text-decoration: none;
    user-select: none;
    -o-user-select: none;
    -moz-user-select: none;
    -khtml-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
    float: left;
    padding: 0;
    width: 32px;
    height: 32px;
}
.fr-toolbar .fr-command.fr-btn.fr-disabled, .fr-popup .fr-command.fr-btn.fr-disabled {
    color: #33cccc;
    cursor: default;
}
.fr-separator {
    background: #2c3e50;
    display: block;
    vertical-align: top;
    float: left;
}
.formRow>dt {
    border-right: 1px solid transparent;
    background: #1d2834;
    border-color: #1d2834;
    text-align: right;
    width: 33%;
    padding: 15px 10px 15px 10px;
}
.overlay {
    position: relative;
    margin: 40px auto 10px;
    margin-top: 4vh;
    width: 100%;
    max-width: 800px;
    background: #2c3e50;
    color: #5b5b5b;
    border-radius: 3px;
    box-shadow: 0 5px 15px 0 rgba(0, 0, 0, 0.5);
    outline: none;
}
.tooltip--member.tooltip--top .tooltip-arrow {
    bottom: 0;
    left: 50%;
    margin-left: -11px;
    border: 11px solid transparent;
    border-bottom-width: 0;
    border-top-color: #2c3e50;
}
.tooltip--member .tooltip-content {
    color: #48adde;
    background: #2c3e50;
    padding: 0;
    text-align: left;
    border: 1px solid #2c3e50;
    box-shadow: 0 5px 10px 0 rgba(0, 0, 0, 0.35);
}
.memberTooltip-name a {
    color: #48adde;
    text-decoration: none;
}
.memberTooltip-header {
    display: table;
    table-layout: fixed;
    width: 100%;
    padding: 6px;
    color: #48adde;
    background: #2c3e50;
}
.memberTooltip-separator {
    margin: -1px 6px 0;
    border: none;
    border-top: 1px solid #48adde;
}
.p-description {
    margin: 5px 0 0;
    padding: 0;
    font-size: 13px;
    color: #48adde;
}

.menu-row.menu-row--alt {
    color: #48adde;
    background: #2c3e50;
}

.menu-header {
    padding: 6px 15px;
    margin: 0;
    font-weight: 400;
    text-decoration: none;
    font-size: 17px;
    color: #48adde;
    background: #2c3e50;
    border-bottom: 1px solid #2c3e50;
}
.block-row.block-row--alt {
    color: #48adde;
    background: #2c3e50;
}
