css:
@-moz-document domain("eddb.io") {
body.site-eddb, body.site-ross {
    background-color: #000;
    color: lightgray;
    font-size: 13px;
}


/* custom scrollbar */
	::-webkit-scrollbar { width: 10px; } /* width */
	::-webkit-scrollbar-track { background: #444; } /* track */
	::-webkit-scrollbar-thumb { background: #888; } /* handle */
	::-webkit-scrollbar-thumb:hover { background: #888; } /* handle hover */

h1 {
    border-bottom-color: #666;
    font-size: 200%;
}

.page-home .page-header-wrapper .page-header h1 {
 	color: #ffbc00;  
    font-weight: 400;
    font-size: 260%;
}

h2 {
  	color: #ffbc00;  
    font-weight: 400;
    font-size: 190%;
}

h3 {
  	color: #CC6400;  
    font-weight: 400;
    font-size: 160%;
}

h2.powerHeadline {
 	color: #ffbc00;  
    font-weight: 400;
}

h2.powerHeadline .powerRank {
	color: #ffbc00;  
    font-weight: 400;
    font-size: 110%;
}

.quick-box {
 	border: 2px solid #1e557d;   
    background-color: #000;
}
	.quick-box:hover {
    	background-color: #222;
    	border: 2px solid #337ab7;
	}

.quick-box .body {
 	color: lightgray;
    font-weight: 400px !important;
}

.quick-box .headline {
 	color: #fff;   
    background-color: #1e557d;
    border-bottom: 0px solid #1e557d;
}
	.quick-box:hover .headline {  
    	background-color: #337ab7;
    	border-bottom: 0px solid #337ab7;
	}

    
a {
	color: #64A8D4;
}

    a:focus {
          color: #72C0F2;
    }

    a:hover {
        color: #72C0F2;
    }

  .alert {
	border: 1px solid #ffbc00;
	padding: 8px;
}

.alert-success {
		color: #ffbc00 ;
		background-color: #222 ;
}

b, strong {
 	font-weight: 400;
    color: #ffbc00;
}

p.warning {
	color: #ffbc00;
}

i {
 	color: #DDD;
    font-weight: 400;
}

.filter-group h3 {
    color: lightgray;
}

.panel {
    background-color: #111;
    color: gray;
}

.panel-eddb {
	border-color: #1E557D;
}    
    
.panel-eddb > .panel-heading {
 	color: #fff;
    background-color: #1E557D;
    border: 1px solid #1E557D;
}

.panel-eddb > .panel-heading .panel-header-action {
 	color: #fff;
    cursor: pointer;
    background-color: #1E557D;
}

.panel-eddb > .panel-heading .panel-heading-link a {
    color: #fff;
    cursor: pointer;
    background-color: #1E557D;
}

.fa-external-link-square:before {
 	color: #fff;   
}

.panel-eddb.with-tabs .nav-tabs > li > a {
	border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    border-color: #1E557D;
}

.panel-eddb.with-tabs .nav-tabs > li.active > a, .panel-eddb.with-tabs .nav-tabs > li.active > a:hover, .panel-eddb.with-tabs .nav-tabs > li.active > a:focus {
 	color:   #fff;
    background-color: #111111;
    border-color: #111111;
}

.panel-eddb.with-tabs .nav-tabs > li > a {
    color: lightgray;
}

.panel-eddb.with-tabs .nav-tabs > li > a:hover {
    color: #fff;
    background-color: #3189C9;
    border-color: #3189C9;
}

.panel-eddb.with-tabs .nav-tabs > li > a:focus {
    color: #fff !important;
    background-color: #3189C9;
    border-color: #3189C9;
}

.modal-header {
	background-color: #292929;
    border-bottom: 1px solid #555;
}
.modal-body {
	background-color: #292929;    
}
.close {
 	font-weight: 400;
    color: #fff;
    opacity: .2;
}
    .close:hover {
        font-weight: 400;
        color: #fff;
        opacity: .4;
    }

.btn-action, .btn-primary {
 	background-color: #1E557D;
    color: #fff;
    font-weight: 400;
    border: 1px solid #1E557D;
}
    .btn-action:hover, .btn-primary:hover {
          background-color: #3189C9;
          color: #fff !important;
          border: 1px solid #3189C9;
    }

.btn-warning {
	background-color: #CC6400;
    color: #fff;
    font-weight: 400;
    border: 1px solid #CC6400;
}
    .btn-warning:hover {
         background-color: #ffbc00;
         color: #fff;
         font-weight: 400;
         border: 1px solid #ffbc00;
    }

form .form-group-withHelp .input-wrapper .btn-help {
	font-size: 90%;
    font-weight: 400;
    height: 16px;
    width: 16px;
    border-radius: 50%;
}

.route-hint {
 	color: #ffbc00;
    font-weight: 400;
    font-size: 100%;
}

.box {
 	border: 2px solid #1E557D;   
}

.box .box-heading {
 	background-color: #1E557D;   
}

.box .box-heading-sub {
 	background-color: #3189C9;   
}


.box .box-body {
    background-color: #111;
    color: lightgray;
}

.loop-list .loop-details .loop-result-wrapper .loop-result-profit small, .loops-result .loop-details .loop-result-wrapper .loop-result-profit small, .single-result .loop-details .loop-result-wrapper .loop-result-profit small {
 	color: lightgray;   
}

.loop-list .loop-details .loop-actions .loop-action-meta, .loops-result .loop-details .loop-actions .loop-action-meta, .single-result .loop-details .loop-actions .loop-action-meta {
 	color: lightgray;
    font-weight: 400;
}
.loop-list .loop-details .loop-result-wrapper .loop-result-profit, .loops-result .loop-details .loop-result-wrapper .loop-result-profit, .single-result .loop-details .loop-result-wrapper .loop-result-profit {
 	font-size: 110%;   
}
.loops-result .loop-details .loop-result-wrapper .loop-result-profit, .single-result .loop-details .loop-result-wrapper .loop-result-profit {
 	background-color: #1E557D !important;  
}

.footer {
 	background-color: #111;
    color: gray;
}

div.FixedHeader_Cloned th {
    background-color: #444 !important;
    color: #DDD;
}

.filter-group .filter-label {
    background-color: #111;
    color: lightgray;
}

.filter-group .filter-value {
 	background-color: #1E557D;
    color: #fff;
    font-weight: 400;
}
.filter-group .filter-value:hover {
 	background-color: #3189C9;
    color: #fff;
    font-weight: 400;
}
.filter-group .filter-enabled .filter-value {
 	background-color: #3189C9;
    color: #fff;
    font-weight: 400;
}

.panel {
    color: #DDD;
}

.panel-subheading {
 	color: #fff;
    background-color: #1E557D;
}

.label-group .label-item.label-name {
	font-size: 13px;
}
  
.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: #202020;
}

.table-striped > tbody > tr:nth-of-type(even) {
    background-color: #262626;
}

input[placeholder] {
    font-size: 100%;
}

.select2-choice, .select2-drop, .select2-search>input{
    background-color: #292929 !important;
    color: #CDCDCD !important;
    border-color: #888 !important;
}

.select2-search>input {
    background-color: #292929 !important;
    background-image: none;
    color: #CDCDCD !important;
    border-color: #888 !important;
}

.modal-content {
 	background-color: #444;
    color: #DDD;
}

.minmax-legend {
 	color: #BBB;   
}

.panel table.panel-table, .panel table.panel-table .number {
 	font-weight: 400 !important;   
    font-size: 13px;
}

.panel table.panel-table .commodity-buy, .FixedHeader_Header table.panel-table .commodity-buy {
    color: #59F;
    font-size: 13px;
}

.worse {
 	color: salmon;   
}

.better {
	color: #3aad14;
}

div.required label:after {
 	color: salmon;   
}

.select2-container .select2-choices, .select2-search-field {
	background-color: #292929 !important;
    color: #CDCDCD !important;
    border-color: #888 !important;
}

.select2-search-field {
	background-color: #292929 !important;
    color: #CDCDCD !important;
    border-color: #888 !important;
}

.select2-search input.select2-active {
    background-color: #292929 !important;
    background-image: url(http://eddb.io/assets/b752da7a/select2-spinner.gif), linear-gradient(#444 85%, #555 99%);
    color: #CDCDCD !important;
    border-color: #888 !important;
}

.select2-container-multi .select2-choices .select2-search-choice {
	background-color: #292929 !important;
    color: #CDCDCD !important;
    border-color: #888 !important;
}

.select2-results .select2-no-results, .select2-results .select2-searching, .select2-results .select2-selection-limit {
    background-color: #292929 !important;
    color: #CDCDCD !important;
    border-color: #888 !important;;
}

.form-control {
    background-color: #292929 !important;
    color: #CDCDCD !important;
    border-color: #888 !important;
}

table.dataTable tbody tr{
    background-color: #333;
}

.panel table.panel-table thead th, .FixedHeader_Header table.panel-table thead th {
 	font-weight: 400 !important;   
}

.panel table.panel-table .route-result-item:nth-child(even), .FixedHeader_Header table.panel-table .route-result-item:nth-child(even) {
    background-color: #444;
}

.stationSearchResult table tbody td a, .systemSearchResult table tbody td a, .factionSearchResult table tbody td a, .bodySearchResult table tbody td a {
 	font-weight: 400;   
}

code {
    background-color: #333;
    color: #FFF;
}

code.changeLog-diff {
	background-color: #333;
    color: #fff;
}

.loops-result .loop-details .loop-actions .loop-action-meta {
    color: #49A;
}


.loops-result .loop-details .loop-result-wrapper .loop-result-profit, .single-result .loop-details .loop-result-wrapper .loop-result-profit{
    background-color: orange;
    color: black;
    
}

.loops-result .loop-details .loop-result-wrapper .loop-result-profit small {
    color: #235;
}

.facilities .facility.no {
    color: salmon
}

.facilities .facility.yes {
    color: #3aad14;
}

.pagination > .active > a {
 	color: #fff !important;
    background-color: #1E557D !important;
    border-color: #1E557D !important;
}



.pagination > .disabled > span, .pagination > .disabled > span:hover, .pagination > .disabled > span:focus, .pagination > .disabled > a, .pagination > .disabled > a:hover, .pagination > .disabled > a:focus {
    background-color: #292929 !important;
    color: #666 !important;
    border: 2px solid #1E557D;
}

.pagination > li > a, .pagination > li > span {
    background-color: #292929;
    color: lightgray;
    border: 2px solid #1E557D;
}

.pagination > li > a:hover, .pagination > li > span:hover {
    background-color: #3189C9;
    color: #fff;
    border: 2px solid #3189C9;
}

.not-set {
    color: #F77;
}

code.changeLog-diff {
    background-color: #444;
}

code.changeLog-diff .changeLog-diff-name {
    color: #AAA;
}

.popover {
 	background-color: #000;   
}

.popover.left > .arrow:after {
    border-left-color: #000;
}

.loop-list .loop-details .loop-result-wrapper .loop-result-profit, .loops-result .loop-details .loop-result-wrapper .loop-result-profit, .single-result .loop-details .loop-result-wrapper .loop-result-profit {
    background-color: orange;
}

.form-control[disabled], form-control[readonly], fieldset[disabled] .form-control {
    background-color: #AAA;
    color: #000;
}

.site-ross .system-presences .system-presence-row.isControlling {
    background-color: #355;
}

.material-outer {
    color: #333;
}

.icon-planet:before {
    color: #CC6400;
}

table.table-eddnlog .action-text i {
    color: white;
}

.stationSearchResult table thead tr th, .systemSearchResult table thead tr th, .factionSearchResult table thead tr th, .bodySearchResult table thead tr th {
    border-right: 2px solid #222;
}

.stationSearchResult table tbody tr:first-child td, .systemSearchResult table tbody tr:first-child td, .factionSearchResult table tbody tr:first-child td, .bodySearchResult table tbody tr:first-child td {
    border-top: 1px solid #222
}

.factionControllingSystems table tr.systemRow td {
 	color: #fff !important;
    background-color: #1E557D;
}

.factionControllingSystems table .systemPresenceTag {
	color: #fff !important;
    background-color: #1E557D;
}

table.table-modules > tbody > tr.row-module-category > td {
 	color: #ffbc00;   
    font-weight: 400;
}

.factionControllingSystems table tr.isHighlighted td {
	background-color: #222;
}

.factionControllingSystems table tr.isHighlighted td .systemPresenceTag {
 	background-color: #CC6400;
}
  
table.table-modules > tbody > tr.row-module-ship > td, table.table-modules > tbody > tr.row-module-group > td, table.table-modules > tbody > tr.row-module-ship > td.module-group-name, table.table-modules > tbody > tr.row-module-group > td.module-group-name {
 	font-size: 110%;   
    font-weight: 400;
}
}
