css:
@-moz-document domain("cloud.google.com") {
/*Global background of near black and text of light gray*/
body, html, .devsite-full-site-width, .devsite-header-upper-tabs .devsite-doc-set-nav-tab {
    color: #c0c0c0;
    background-color: #1b1b1b;
}

/*Top bar with nav items "Why Google", "Solutions", etc...*/
.devsite-doc-set-nav-active {
    border-bottom-color: #1a73e8;
    color: #c0c0c0;
}

/*Underlines under tab parts on top bar.*/
.devsite-doc-set-nav-tab {
    border-bottom: 2px solid #c0c0c0;
    border-bottom-color: rgb(192, 192, 192);
}

/*"AI & Machine Learning Products" color on top left here:  https://cloud.google.com/ml-engine/docs/tensorflow/getting-started-training-prediction*/
.devsite-doc-set-nav-row>.devsite-secondary-branding {
    color: #aebfd4;
}

/*"Contact Sales" button at the top right.*/
.cta-button-secondary {
    background-color: #333;
    border: 1px solid #909090;
    border-top-color: #909090;
    border-top-style: solid;
    border-top-width: 1px;
    border-right-color: #909090;
    border-right-style: solid;
    border-right-width: 1px;
    border-bottom-color: #909090;
    border-bottom-style: solid;
    border-bottom-width: 1px;
    border-left-color: #909090;
    border-left-style: solid;
    border-left-width: 1px;
    border-image-source: initial;
    border-image-slice: initial;
    border-image-width: initial;
    border-image-outset: initial;
    border-image-repeat: initial;
    color: #fff;
    font-size: 14px;
}

/*Left tree view*/
.devsite-section-nav {
    background: #303030;
    background-color: rgb(48, 48, 48);
    /*color: #c0c0c0;*/
}

/*Right list (disappars when browser is reduced in width)*/
.devsite-page-nav {
    background: #303030;
    background-color: rgb(48, 48, 48);
}

/*Title text items in tree view (root note text color) - make it brighter than the rest of the text.*/
.devsite-nav-item-heading>.devsite-nav-title {
    color: #e0e0e0;
}

/*Light gray text in tree view.*/
.devsite-nav-title {
    color: #c0c0c0;
}

/*Large content area on the right.*/
.devsite-article-body {
    background-color: #303030;
    color: #c0c0c0
}

/*Code container sections*/
.kd-tabbed-horz, .kd-tabbed-vert, .ui-datasection-main {
    background: #303030;
    background-color: #303030;
    border-top-color: rgb(87, 87, 87);
    border-right-color: rgb(87, 87, 87);
    border-bottom-color: rgb(87, 87, 87);
    border-left-color: rgb(87, 87, 87);
}

/*Gray code boxes*/
code, pre, .pre-style {
    background: #505050;
    background-color: rgb(80, 80, 80);
    color: #88afc3;
}

/*Hot links inside code blocks*/
a code, td a code {
    color: #b6d2f7;
}

/*Visited hot links, site wide*/
:link, :visited {
    color: #61a5ff;
}

/*Button color*/
.button-blue, .button-green, .button-primary, .button-red, #embedded-explorer .com-google-api-explorer-client-embedded-EmbeddedParameterForm_EmbeddedParameterFormUiBinderImpl_GenCss_style-execute {
    background-color: #004263;
}

/*Link text color in tabs inside code containers*/
.kd-tabbutton.selected {
    border-bottom-color: #1a73e8;
    color: #77b2ff;
}

/*Command line box color*/
.kd-tabbed-horz>article>pre, .kd-tabbed-vert>article>pre, .ui-datasection-main>section>pre {
    background: #505050;
    background-color: rgb(80, 80, 80);
}

/*Caution box*/
.caution, .caution code, .caution :link, .caution :visited {
    background: #fff3e036;
    background-color: rgba(255, 243, 224, 0.21);
    color: #fb9d85;
}

/*Node box*/
aside, aside code, aside var, aside :link, aside :visited, .note, .note code, .note :link, .note :visited, .special, .special code, .special :link, .special :visited {
    background: #454b4e;
    background-color: rgb(69, 75, 78);
    color: #6dc1ef;
}

/*Warining box*/
.warning, .warning code, .warning :link, .warning :visited, #embedded-explorer .com-google-api-explorer-client-history-EmbeddedHistoryItemView_HistoryItemUiBinderImpl_GenCss_style-error, #embedded-explorer .com-google-api-explorer-client-history-EmbeddedHistoryItemView_HistoryItemUiBinderImpl_GenCss_style-error :link, #embedded-explorer .com-google-api-explorer-client-history-EmbeddedHistoryItemView_HistoryItemUiBinderImpl_GenCss_style-error :visited {
    background: #fff3e036;
    background-color: rgba(255, 243, 224, 0.21);
    color: #fb9d85;
}

/*key words in code blocks*/
pre .kwd, pre .tag {
    color: #e8f0ff;
}

/*Numerical constants in clode blocks.*/
#embedded-explorer .com-google-api-explorer-client-Resources-Css-jsonNumber, pre .lit, pre .dec {
    color: #ea7c6f;
}

/*Drop-down buttons*/
.devsite-utility-footer-language .kd-select {
    background: #848484;
    background-color: rgb(132, 132, 132);
}
}
