/* edit as much as you want and feel free to post the source. */
 html {
    font-family:'Roboto', sans-serif;
}
/* title */
 .p-header {
    color: #ffffff;
    background-color: #202123;
}
/* menu bar */
 .p-staffBar {
    color: #ffffff;
    background-color: #202123;
}
.p-navgroup-linkText {
    color: #ffffff;
}
.p-navgroup {
    color: #ffffff;
    background-color: #202123;
}
.p-header-logo {
    color: #ffffff;
    background-color: #202123;
}
.p-header-logo--text {
    color: #ffffff;
    background-color: #202123;
}
.p-breadcrumbs--container {
    color: #ffffff;
    background-color: #202123;
}
.p-breadcrumbs>li a {
    display: inline-block;
    vertical-align: bottom;
    max-width: 300px;
    color: #ffffff;
    overflow: hidden;
    white-space: nowrap;
    word-wrap: normal;
    text-overflow: ellipsis;
}
/* posts bar */
 .hScroller-scroll {
    opacity: 0;
    /* Making the new posts bar transparent */
}
