css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("swordandgame.blogspot.com") {

.main-inner, .tabs-inner {
    max-width: 1560px;
    color: #aaa;
}

a[imageanchor] {
    float: right !important;
}

.date-outer {
    background-color: #222;
}

blockquote {
    background-color: #222;
    border-color: #0af;
}

#comments {
    display: none;
}

img {
    background-color: #222 !important;
}

.separator + h3 + span,
.separator + div + span,
.separator + h3 + div span,
.separator + h3 + span + br + span {
    background-color: #222 !important;
    color: #f8a !important;
}

}
