css:
@-moz-document domain("fontlibrary.org") {

body {
    background: #e9ecef;
    font-family: 'Open Sans', "Segoe UI", Roboto, 'Helvetica Neue', sans-serif;
    font-size: medium;
    line-height: 1.4em;
    color: #495057;
}


a:link,
a:visited,
a:active {
    color: #0c8599;
}
a:hover {
    color: #0b7285;
}
hr {
    border-top-color: #dee2e6;
}
input[type="submit"].button,
#information a.sign-up,
.pagination a,
a.pusher,
.submit_button,
a.cancel,
a.btn,
#information p.download > a.pusher {
    background-color: #0c8599;
    color: #fff;
    text-shadow: 1px 1px 1px #0b7285;
}
h2 {
    font-size: 2.2em;

    text-transform: none;
    letter-spacing: 0;
    text-shadow: 0 0 3px #fff;
    font-weight: lighter;
    line-height: 1;
}
h3,
#information ul li.heading,
form label {

    text-shadow: none;
    color: #495057;
    display: block;
}
h3 {
    text-transform: none;
    font-size: 1.75em;
    letter-spacing: 0;
    font-weight: normal;
    padding: 1em 0 .5em 0;
}


/*---Layout---*/
#textblock {
    background-color: #f1f3f5;
}
#head {
    background-color: #495057;
}
#head ul.primary li a,
#head ul.secondary li a {
    margin: 0;
    font-size: 0.9em;
    line-height: 1;
    display: inline-block;
    text-transform: uppercase;
    letter-spacing: .05em;
    padding: 29px 5px 13px;
    color: #f1e5d1;
    text-shadow: 1px 1px 1px #495057;
}


/*---Nav___*/
#head ul.primary li a,
#head ul.secondary li a {

    letter-spacing: 0;
    padding: 29px 5px 13px;
    color: #f1f3f5;
}
#head ul.primary li a:hover,
#head ul.secondary li a:hover {

    color: #fff;
}
#head ul.primary li a.active,
#head ul.secondary li a.active {
    background-color: #0b7285;
}

.toolbar select,
#head ul.secondary .lang select {
    background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100' height='100' fill='%230b7285'><polygon points='0,0 100,0 50,50'/></svg>");
    background-color: #f8f9fa;
    outline: 0;
    border: 1px solid #adb5bd;

    color: #495057;
}
#head ul.secondary .search input[type="text"] {
    border-radius: 3px 0 0 3px;
}

@media screen and (-webkit-min-device-pixel-ratio: 0) {
    #head ul.secondary .search input[type="text"] {
        height: auto;
    }
}

#head ul.secondary .search .search-button {
    background-color: #0b7285
}
#logo {
    background: url(https://upload.wikimedia.org/wikipedia/commons/a/a9/Open_Font_Library_logo.svg) #fff no-repeat;
    background-size: 80px 75px;
    background-position: center center;
    border-radius: 0 0 5px 5px;
    box-shadow: 1px 2px 5px #adb5bd;
    width: 100px;
}


/*-----Home-----*/
#feature .tabs {

    background: #99e9f2;
}
#feature .tabs > label {
    font-size: 11px;
    color: #0b7285;
}

#foot {

    border-top: 1px solid #3bc9db;
    background: #ced4da;
}
#foot p.logo {
    margin-top: 20px;
}

/*---Single font---*/
ul#font-variants li a:hover,
ul#font-variants li a:active {
    color: #0b7285;
    background-color: #fff;
}
ul#font-variants li {
    border-bottom-color: #dee2e6
}
form input[type="text"],
form input[type="email"],
form input[type="url"],
form textarea,
form input[type="password"],
input[type="text"],
textarea,
input[type="password"] {

    border: 1px solid #ced4da;
    padding: 10px 5px;
    font-size: .8em;
    color: #495057;
    box-sizing: border-box;

    border-radius: 3px;
}

input[type="text"].placeholder {
    color: #adb5bd;
}

input[type="text"]::-webkit-input-placeholder {
    color: #adb5bd;
}

input[type="text"]:-moz-placeholder {
    color: #adb5bd;
}
#information h3 {
    border-bottom: 1px solid #ced4da;
    border-top: 1px solid #ced4da;
    padding: 6px 0;
    margin-bottom: 5px;
    font-size: 1em;
    text-transform: uppercase;
    font-weight: bold;
}
#font-detail-content .waterfall {

    border-right: 1px solid #ced4da;
    box-shadow: inset 1px 1px 3px #ced4da;
    -moz-box-shadow: inset 1px 1px 3px #ced4da;
    -webkit-box-shadow: inset 1px 1px 3px #ced4da;
}
.fonts .element ul.primary,
.font .element ul.primary {
    font-size: x-small;
    background: #ced4da;
    color: #495057;
}
ul li.heading,
#font-detail-content h4 {

    color: #495057;
}
form.preview {
    background: none;
}
}
