css:
/* i really want this to be global */
.html5-play-progress, .ytp-play-progress {
   background: url("https://imgur.com/EbLOWqY") repeat-x !important; 
background: linear-gradient(to bottom, #FFFFFF 0%, #FFFFFF 16.5%, #FFFFFF 16.5%, #FFFFFF 33%, #FFFFFF 33%, #FFFFFF 50%, #FFFFFF 50%, #FFFFFF 66%, #FFFFFF 66%, #FFFFFF 83.5%, #FFFFFF 83.5%, #FFFFFF 100%) !important;
background: -webkit-linear-gradient(top, #FFFFFF 0%, #FFFFFF 16.5%, #FFFFFF 16.5%, #FFFFFF 33%, #FFFFFF 33%, #FFFFFF 50%, #FFFFFF 50%, #FFFFFF 66%, #FFFFFF 66%, #FFFFFF 83.5%, #FFFFFF 83.5%, #FFFFFF 100%) !important;
background: -moz-linear-gradient(top, #FFFFFF 0%, #FFFFFF 16.5%, #FFFFFF 16.5%, #FFFFFF 33%, #FFFFFF 33%, #FFFFFF 50%, #FFFFFF 50%, #FFFFFF 66%, #FFFFFF 66%, #FFFFFF 83.5%, #FFFFFF 83.5%, #FFFFFF 100%) !important;
}

.html5-load-progress, .ytp-load-progress {
   background:  url("https://i.imgur.com/1EBdUmM.png") !important;
}

.html5-scrubber-button, .ytp-scrubber-button {
   background: url("https://i.imgur.com/yQAZrXs.gif") !important;
   width: 34px !important;
   height: 21px !important;
   border: none !important;
   margin-left: -18px !important;
   margin-top: 0px !important;
   transform: scale(0.8);
-webkit-transform: scale(0.8);
-moz-transform: scale(0.8);
-ms-transform: scale(0.8);
}
.ytp-progress-bar-container:hover .ytp-scrubber-button,
.ytp-progress-bar-container:hover .ytp-load-progress{
	image-rendering: pixelated;
}
.html5-progress-bar-container, .ytp-progress-bar-container {
   height: 12px !important;
}
.html5-progress-bar, .ytp-progress-bar {
   margin-top: 12px !important;
}
.html5-progress-list, .ytp-progress-list, .video-ads .html5-progress-list.html5-ad-progress-list, .video-ads .ytp-progress-list.ytp-ad-progress-list {
   height: 12px !important;
}
.ytp-volume-slider-track {
   background: #0C4177 !important;
}
