css:
/* ==UserStyle==
@name         An almost complete remake 2.0 of css for the game Pardus with more of everything.
@namespace    https://userstyles.org/styles/167883/pardus-style-remake-2-0
@version      2.0.2
@license      CC BY-SA - Creative Commons Attribution-ShareAlike
==/UserStyle== */

@-moz-document domain("pardus.at") {
/* Configuration */

/* set ship in dock image */
img[title~="dock."] {
    background:url(https://i.pinimg.com/originals/85/5c/75/855c75d1823f30bd65d2d78c4d0338ed.jpg);
    background-position: center top;
}

/* Remove below if you want to exchange certain skills for ASP */
input[value="maneuver"]+input {
    pointer-events: none;
    filter: saturate(0) contrast(.75);
} 
input[value="engineering"]+input {
    pointer-events: none;
    filter: saturate(0) contrast(.75);
} 

/* get teuton normal CE font and install in your system */
/* get teuton normal CE at http://gramajda.hekko24.pl/p/pardus/teutonnormalce.ttf */
/* if the font doesn't work after installed, 'Rationale' is similiar, but a little too narrow fallback font */
@font-face {
  font-family: 'Rationale';
  font-style: normal;
  font-weight: 400;
  src: local('Rationale'), local('Rationale-Regular'), url(https://fonts.gstatic.com/s/rationale/v10/9XUnlJ92n0_JFxHIfHcccVtN.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}

body, table, th, td, div, a, span, input, button, select {
    font-family: 'teuton normal CE', 'Rationale', arial, sans-serif !important;
}
body > table {
    font-size: 1.25em !important;
}
table, span {
    font-size: inherit !important;
}
input, button, select {
    font-size: 1em !important;
}
font[size="1"] {
    font-size: 13px;
}
#ipbwrapper,
#ipbwrapper .postcolor,
#ipbwrapper td {
    font-size: 16px;
}
/* Remove above font rules if you don't have teutonnormalce.ttf installed or don't like Rationale font */

/* manual */
@media (min-width: 1330px) {
  #rand {
    margin: auto;
    transform: scale(1.35);
    transform-origin: 50% 0;
  }
}

/* End of configuration */

::-webkit-scrollbar {
    background: #080F13;
    width: 14px;
}
::-webkit-scrollbar-thumb {
    background: #074065;
    border: 1px solid #080F13;
    border-radius: 2ex;
}

:root {
  --text-color: #A6D1F6;
  --action-color: #8DF4F5;
  --link-color: #CBFFF8;
  --link-rgb: 203, 255, 248;
  --main-blue-color: #2776C3;
  --teal-color: #308178;
  --teal-rgb: 57, 153, 141;
}

html {
    position: relative;
    scrollbar-color: #074065 #080F13;
}
body {
    background:url(http://gramajda.hekko24.pl/p/background.png) #111 !important;
    color: var(--text-color) !important;
    margin-top:0px !important;
    margin-bottom:0px !important;
}
table {
    color: var(--text-color) !important;
}

/* huge screen spacing */
td[height="58"][width="30"] img {
    width: calc(50vw - 850px) !important
}
body > table > tbody > tr > td[width="25%"]:first-child {
    padding-left: calc(50vw - 840px) !important
}
body > table > tbody > tr > td[width="25%"]:last-child {
    padding-right: calc(50vw - 840px) !important
}
#tdTabsLeft {
    padding-left: calc(50vw - 850px) !important
}
#tdTabsRight {
    padding-right: calc(50vw - 850px) !important
}

/* panels */
img[src*="/actions.png"] {
background:url(http://gramajda.hekko24.pl/p/actions.png) !important;
width:0px !important;
height:0px !important;
padding: 23px 110px 23px 110px !important;
}
img[src*="/status.png"] {
background:url(http://gramajda.hekko24.pl/p/status.png) !important;
width:0px !important;
height:0px !important;
padding: 23px 110px 23px 110px !important;
}
img[src*="/cargo.png"] {
background:url(http://gramajda.hekko24.pl/p/cargo.png) !important;
width:0px !important;
height:0px !important;
padding: 23px 110px 23px 110px !important;
}
img[src*="/commands.png"] {
background:url(http://gramajda.hekko24.pl/p/commands.png) !important;
width:0px !important;
height:0px !important;
padding: 23px 110px 23px 110px !important;
}
img[src*="/otherships.png"] {
background:url(http://gramajda.hekko24.pl/p/otherships.png) !important;
width:0px !important;
height:0px !important;
padding: 23px 110px 23px 110px !important;
}
img[src*="/ship.png"] {
background:url(http://gramajda.hekko24.pl/p/ship.png) !important;
width:0px !important;
height:0px !important;
padding: 23px 110px 23px 110px !important;
}
img[src*="/position.png"] {
background:url(http://gramajda.hekko24.pl/p/mapperdata.png) !important;
width:0px !important;
height:0px !important;
padding: 23px 110px 23px 110px !important;
}
td[style*="/panel.png"] {
background:url(http://gramajda.hekko24.pl/p/panel.png) !important;
background-repeat:repeat-y !important;
padding-left:6px !important;
}
img[src*="/panelbottom.png"] {
background:url(http://gramajda.hekko24.pl/p/pbottom.png) !important;
width:0px !important;
height:0px !important;
padding: 8px 110px 8px 100px !important;
margin: 0px 0px 0px 7px !important;
}
td[valign=top] > table[width="210"] {
margin:-11px 6px -15px 8px;
}
#status_content {
    margin: auto !important;
    max-width: 180px;
}
#status_content > table {
    width: 100%;
}
#credits {
    display: inline-block;
    margin-left: -10px;
}
#aCmdCollect2,
#aCmdCollect5 {
    position: relative;
    z-index: 1;
    left: 10px;
    margin-left: -10px;
    margin-right: -30px;
    color: black !important;
    font-weight: bold;
    font-size: 12px;
}
#aCmdCollect5 {
    left: 51px;
}
#aCmdCollect2:before,
#aCmdCollect5:before {
    content: '';
    box-sizing: content-box;
    position: absolute;
    top: -2px;
    bottom: -1px;
    left: -1px;
    right: -2px;
    background: var(--action-color);
    box-shadow: 0 0 5px rgba(0,0,0,1);
    border-radius: 2px 1.5px;
    transform: skew(-7deg);
    opacity: .8;
    transition: all .2s;
    z-index: -1;
}
#aCmdCollect2:hover:before,
#aCmdCollect5:hover:before {
    opacity: 1;
}
#otherships[style="display: block;"] {
    display: table !important;
}
#otherships:not([style="display:none;"]) + #yourposition {
    top: 21px;
}
#yourposition {
    position: relative !important;
    animation: emerge .1s linear;
}
#yourposition #mini_map_settings {
    right: 13px;
    top: 10px;
    border-radius: 50% 2px 50% 50%;
    z-index: 1;
    border: 0px solid #aaa;
    box-sizing: border-box;
    opacity: .7;
}
#yourposition:after {
    content: '';
    position: absolute;
    right: 9px;
    top: 6px;
    padding: 9px;
    border: 2px solid var(--teal-color);
    border-width: 2px 2px 1px 1px;
    box-shadow: 0 0 0px 2px #000, 0 0 0px 3px #002A64;
    border-radius: 50% 3px 50% 50%;
}
#yourposition td[style][align="center"] {
    padding: 0 11px 0 19px !important;
}
#yourposition #miniMapper {
    width: auto !important;
    margin-bottom: -2px;
}
#mini_map_coords:not(:empty) {
    top: auto !important;
    bottom: -7px;
    left: 42%;
    background: #111;
    border: 1px solid var(--teal-color);
    padding: 3px 5px 2px;
    border-radius: 1em;
    font-size: 13px !important;
}

/* nav page & fields */
#tdSpaceChart:before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    left: 0;
    height: calc(100% + 40px);
    min-height: 100vh;
    z-index: -1;
    background: url(http://gramajda.hekko24.pl/p/pardus/spacebg.jpg) top center;
	opacity: .7;
}
#tdSpaceChart > table[align] {
    background: rgba(9, 21, 40, .6);
    border: 1px solid rgba(57, 153, 141, .7) ;
    border-radius: 16px;
    padding: 0px 14px 5px 0;
    margin-top: -4px;
}
#tdSpaceChart td[style="background-color:#00001C;"] > div[style] {
    overflow: visible !important;
}
#tdSpaceChart table[style] tr:nth-child(2n) td[class]:first-child {
	box-shadow: -1px 0 0 rgba(0,0,0, .9),
                -3px 0 0 rgba(var(--teal-rgb), .7);
}
#tdSpaceChart table[style] tr:nth-child(2n) td[class]:last-child {
	box-shadow: 1px 0 0 rgba(0,0,0, .9),
                3px 0 0 rgba(var(--teal-rgb), .7);
}
#tdSpaceChart table[style] tr:first-child td[class]:nth-child(2n) {
	box-shadow: 0 -1px 0 rgba(0,0,0, .9),
                0 -3px 0 rgba(var(--teal-rgb), .7);
}
#tdSpaceChart table[style] tr:last-child td[class]:nth-child(2n) {
	box-shadow: 0 1px 0 rgba(0,0,0, .9),
                0 3px 0 rgba(var(--teal-rgb), .7);
}
table#navarea,
table#navareatransition {
border-spacing: 1px;
background:#111!important;
}
table#navarea td,
table#navareatransition td {
background-repeat:no-repeat !important;
background-position:center center !important;
}
table#navarea a,
table#navareatransition a {
    display: block;
	position: relative;
	overflow: hidden;
    cursor: pointer;
}
table#navarea a:hover:before,
table#navareatransition a:hover:before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background:url(http://gramajda.hekko24.pl/p/navcoordbg1.png) -1px 0 no-repeat !important;
}
table#navareatransition .navClear a:hover:before {
    z-index: 1;
}
#tdSpaceChart td[style] > a > img {
	position: relative;
}
table#navarea a:active:after,
table#navareatransition a:active:after {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
	border-radius: 50%;
	background: var(--main-blue-color);
	opacity: 0;
	animation: pulse .2s linear;
}
@keyframes pulse {
  0% {
    opacity: .8;
    transform:scale(0);
  }
  100% {
    opacity: 0;
    transform:scale(2);
  }
}
/* tile highlighter */
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(4):nth-last-child(4) > div[style],
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(5):nth-last-child(5) > div[style],
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(5):nth-last-child(5) > div[style],
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(6):nth-last-child(6) > div[style] {
    background: transparent !important;
}
#tdSpaceChart td[id] {
    overflow: hidden;
}
#tdSpaceChart td[id] > div[style]:empty {
    display: none;
}
#tdSpaceChart td[id] > div[style] {
    margin: -9px;
    padding: 9px;
    border-radius: 10em;
}
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(4):nth-last-child(4) a,
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(5):nth-last-child(5) a,
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(5):nth-last-child(5) a,
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(6):nth-last-child(6) a {
    position: relative;
    z-index:1;
}
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(4):nth-last-child(4) a:before,
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(4):nth-last-child(4) a:after,
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(5):nth-last-child(5) a:before,
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(5):nth-last-child(5) a:after,
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(5):nth-last-child(5) a:before,
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(5):nth-last-child(5) a:after,
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(6):nth-last-child(6) a:before,
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(6):nth-last-child(6) a:after {
    content: '';
    position: absolute;
    border: 10px solid green; 
    top: 4px;
    left: 4px;
    bottom: 4px;
    right: 4px;
    z-index: -1;
    border-radius: 50%;
    border-color: rgba(0, 255, 0,.2) transparent;
    animation: spin 15s linear infinite;
}
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(4):nth-last-child(4) a:after,
#tdSpaceChart tr:nth-child(4):nth-last-child(4) td:nth-child(5):nth-last-child(5) a:after,
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(5):nth-last-child(5) a:after,
#tdSpaceChart tr:nth-child(5):nth-last-child(5) td:nth-child(6):nth-last-child(6) a:after {
    top: -9px;
    left: -9px;
    bottom: -9px;
    right: -9px;
    border: 17px ridge rgba(40, 113, 194,.4);
    animation: spin 9s linear infinite;
    animation-direction: reverse;
}
    @keyframes spin { 
        from { 
            transform: rotate(0deg); 
        } to { 
            transform: rotate(360deg); 
        }
    }

/* top menu frame */
table[style="height:58px;"] td {
    background-image:none !important;
}
table[style="height:58px;"] td td:not(.menubutton) {
    width: 6px;
    max-width: 6px;
    min-width: 6px;
}
.menubutton {
    width: auto !important;
}
td.menubutton > a {
    position: relative;
    z-index: 1;
    height:58px;
    width: auto;
    text-align:center;
    background: none !important;
    cursor: pointer !important;
}
.menubutton > a:before,
.menubutton > a:after{
    content: '';
    position: absolute;
    z-index: -1;
    top: 1px;
    bottom: 22px;
    border-radius: 6px;
    box-shadow: inset 0 0px 0px rgba(15, 45, 83, 0);
    transition: all .2s;
}
.menubutton a:before {
    transform: skew(15deg);
}
.menubutton a:after {
    transform: skew(-15deg);
}
.menubutton a:after,
.menubutton:nth-child(4n) a:before {
    left: 40%;
    right: -1px;
    background: linear-gradient(to right, rgb(15, 45, 83) 45%, rgba(13, 100, 196, .9)) !important;
}
.menubutton a:before,
.menubutton:nth-child(4n) a:after {
    right: 40%;
    left: -1px;
    background: linear-gradient(to left, rgb(15, 45, 83) 45%, rgba(13, 100, 196, .9)) !important;
}
.menubutton a:hover:before,
.menubutton a:hover:after{
    box-shadow: inset 0 -25px 40px -20px rgb(7, 22, 43);
    filter: contrast(1.1) saturate(1.1);
}
td[height="58"] img {
    height:0px !important;
    filter: contrast(1.2) saturate(.8);
}
.menubutton .img1 {
display: inline-block !important;
padding:25px 57px;
margin: 0px -10px;
background-position: center -7px;
background-repeat: no-repeat;
}
.menubutton .img2 {
display: none !important;
}
img[alt="Navigation"] {
background-image:url(http://gramajda.hekko24.pl/p/nav.png)!important;
}
img[alt="Overview"] {
background-image:url(http://gramajda.hekko24.pl/p/overview.png)!important;
}
img[alt="Messages"] {
background-image:url(http://gramajda.hekko24.pl/p/messages.png)!important;
}
img[alt="News"] {
background-image:url(http://gramajda.hekko24.pl/p/news.png)!important;
}
img[alt="Diplomacy"] {
background-image:url(http://gramajda.hekko24.pl/p/diplomacy.png)!important;
}
img[alt="Statistics"] {
background-image:url(http://gramajda.hekko24.pl/p/statistics.png)!important;
}
img[alt="Options"] {
background-image:url(http://gramajda.hekko24.pl/p/options.png)!important;
}
img[alt="Forum"] {
background-image:url(http://gramajda.hekko24.pl/p/forum.png)!important;
}
img[alt="Chat"] {
background-image:url(http://gramajda.hekko24.pl/p/chat.png)!important;
}
.menu-sublinks {
    position: absolute;
    top: 37px;
    margin-left: -6px;
    z-index: 1;
}
.menubutton .menu-sublinks a {
    display: inline-block;
    width: auto;
    height: auto;
    padding: 1px 5px;
    line-height: 17px;
    border: 1px solid #052549;
    background: #00112A;
    font-size: 13px;
    cursor: pointer !important;
}
.menubutton .menu-sublinks a:first-letter {
    font-size: inherit;
}


/* headers */
.first,
tr.first {
    background-color: transparent;
}
tr th {
    white-space:nowrap;
    color:#00001C !important;
    background-color: transparent !important;
    background-image:
      repeating-linear-gradient(84deg,
        transparent 2.4px,
        hsla(198, 30.9%, 73.3%, .8) 2.6px 40%,
        transparent 90% 100%),
      repeating-linear-gradient(-84deg,
        transparent 2.4px,
        hsla(198, 30.9%, 73.3%, .7) 2.6px 40%,
        transparent 90% 100%);
}
table[style] th:only-child,
tr:first-child > th:only-child,
tr:first-child > td[align="center"][style="border-style:solid; border-width:1px; background-color:#000044; color:#DDDDFF;"] {
    padding:4px 36px 4px;
    min-width: 64px;
    border-width: 0 !important;
    border-radius: 2.5px 2.5px 7px 7px / 3px 3px 12px 12px;
    font-size:16.7px;
    color:#00001C !important;
    font-variant:small-caps;
    text-shadow: 0 0 1.5px rgba(255,255,255,.25);
    background:
      repeating-linear-gradient(80deg,
        transparent 0 16%,
        rgba(39,118,195,1) 16% 55%,
        transparent 55% 100%),
      repeating-linear-gradient(-80deg,
        transparent 0 16%,
        rgba(39,118,195,1) 16% 55%,
        transparent 55% 100%),
      repeating-linear-gradient(80deg,
        transparent 0 4px,
        rgba(39,118,195, .8) 4px 25px,
        transparent 25px 35px,
        rgba(39,118,195, .8) 35px 70px,
        transparent 70px 80px,
        rgba(39,118,195, .8) 80px 40%,
        transparent 40px 100%),
      repeating-linear-gradient(-80deg,
        transparent 0 4px,
        rgba(39,118,195, .8) 4px 25px,
        transparent 25px 35px,
        rgba(39,118,195, .8) 35px 70px,
        transparent 70px 80px,
        rgba(39,118,195, .8) 80px 40%,
        transparent 40% 100%),
      repeating-linear-gradient(0deg,
        transparent 0 2px,
        rgba(0,0,0, .3) 2px 3px),
      repeating-linear-gradient(90deg, 
        transparent 0,
        rgba(39,118,195, .2) 10% 90%,
        transparent 100%) !important;
}
#building_man .messagestyle > tbody > tr:first-child > th {
    padding:4px 36px 4px !important;
}
table.messagestyle th:only-child:empty {
    min-width: 0;
    padding:0;
}
.messagestyle th:not(:only-child),
tr:first-child th:not(:only-child),
tr.first td:not(:only-child) {
    padding:3px 4px 3px 3px;
    font-size:16.6px;
    font-variant:small-caps;
    font-weight: bold;
    border: 0;
    border-radius: 1.6px 1.6px 4px 4px / 1.6px 1.6px 6px 6px;
    text-shadow: 0 0 1.5px rgba(255,255,255,.25);
    background-image:
      repeating-linear-gradient(84deg,
        transparent 2.4px,
        rgba(166, 195, 208, .85) 2.6px 15%,
        transparent 55% 100%),
      repeating-linear-gradient(-84deg,
        transparent 2.4px,
        rgba(166, 195, 208, .7) 2.6px 60%,
        transparent 90% 100%);
}
.marginstyle .first td {
    text-transform: lowercase;
}
.marginstyle .first td:first-letter,
.marginstyle .first a:first-letter {
    text-transform: uppercase !important;
}
.messagestyle th:not(:only-child):nth-child(2n),
tr:first-child th:not(:only-child):nth-child(2n),
tr.first td:nth-child(2n) {
    border-radius: 4px 4px 1.6px 1.6px / 6px 6px 1.6px 1.6px;
    background-image:
      repeating-linear-gradient(96deg,
        transparent 2.4px,
        rgba(166, 195, 208, .85) 2.6px 15%,
        transparent 55% 100%),
      repeating-linear-gradient(-96deg,
        transparent 2.4px,
        rgba(166, 195, 208, .7) 2.6px 60%,
        transparent 90% 100%);
}
tr:last-child > th:only-child {
    border-radius: 0;
    padding: 5px 2px 0;
    box-shadow: 0px -1px 0 0 var(--main-blue-color);
    background: transparent !important;
    color: inherit !important;
    font-variant: normal;
    font-size: 15px;
    line-height: 180%;
}
tr:last-child > th:only-child font {
    color: inherit !important;
}
tr th th, td+th {
    border: 0;
    background: transparent !important;
    color: inherit !important;
    padding: 1px 0 !important;
}
th[style*="white-space: nowrap"] {
background:url(http://gramajda.hekko24.pl/p/background2.png) !important;
color: var(--text-color) !important;
}
h1::first-letter {
