/* building index  */
table#minimap:active {
    opacity: .1;
}
table#minimap {
    display: block;
    bottom: 0;
    top:0px !important;
    left:auto !important;
    right: 0 !important;
    padding-right: 6px;
    border: 0;
    border-spacing: 1px !important;
    background: transparent;
    opacity: 1;
}
table#minimap:before {
    content: '';
    position: absolute;
    top: 0;
    bottom: 0;
    right: 0;
    left:-100%;
    background: linear-gradient(rgba(0,0,28,1),rgba(0,0,28,.5)) !important;
    box-shadow: 0 0 10px rgba(0,0,0,0.5);
}
table#minimap tbody {
    display: inline-table;
    transform: translate(-50%, -50%) scale(2);
    position: relative;
    top: 50%;
}
