    background-color:#777 !important;
}
table#minimap td[style*="255, 255, 255"]:after,
table#minimap td[style*="#ffffff"]:after {
    content: '';
    position: absolute;
    top: -6px;
    right: -6px;
    bottom: -6px;
    left: -6px;
	border-radius: 50%;
	background: var(--main-blue-color);
	z-index: 1;
