/* pardus fast bot use */
div#cargo_content {
position:relative;
}
div#cargo_content table div {
position:absolute;
top:-9px;
left:-38px;
background:url(http://gramajda.hekko24.pl/p/bots-addon.png) no-repeat left top;
height:46px;
padding:10px 0px 0px 1px;
text-align:center;
line-height:1.15em;
}
div#cargo_content table div a:first-line {
font-size:12px !important;
}
div#cargo_content table div a {
font-size:20px !important;
display:block !important;
white-space:normal !important;
}

a[href="distresscall.php"] ~ font {
position: relative;
bottom: -15px;
}
#bots-container {
position:absolute;
top:100%;
left:-370px;
right:-370px;
margin: auto;
padding: 0 !important;
background: none !important;
text-align: center;
}
#bots-container a {
min-width:33px;
line-height: 60%;
font-size:14px;
text-align:center;
padding:2px 0px;
margin:10px 6px;
}
#bots-container a:hover {
box-shadow: 0px 0px 15px 5px black;
z-index: 1;
}
#bots-container a i {
    position: relative;
    top: 4px;
    color: #000;
    text-shadow: none;
    font-size: 10px;
    font-style: normal;
}

form[method='post'][action*='combat.php'] {
    position: relative;
    display: block !important;
}
#rounds-container {
position:absolute;
right:50%;
top: 30px;
margin: 0px -235px 0px 0px !important;
width:470px;
background:#00001C;
}
#rounds-container a {
display:block;
float:left;
color:#93DAEF !important;
font-size:22px;
line-height:72%;
padding-top:2px;
text-align:center;
width:43px;
height:30px;
margin:1px;
border:1px var(--main-blue-color) outset;
border-radius: 3px;
background:url(http://gramajda.hekko24.pl/p/button2.png) center 9px #144A91 !important;
}
#rounds-container a:hover {
