}
#rounds-container a span {
color:#5FBAF3;
font-size:10px !important;
white-space:nowrap;
}

/* PardusOverlay */
table[width^='210'][id]:hover {
position:relative; z-index:20;
}
div[id*='closecontainer']:hover {
position:relative; z-index:15;
}
div[onclick*=\"showodp\"] {
background-position:0px center !important; cursor:pointer;
}
div[onclick*=\"showodp\"]:hover {
background-position:-34px center !important;
}
div[id*='closecontainer'] div[onclick] {
color:#55B2F3 !important; cursor:pointer;
}
div[id*='closecontainer'] div[onclick]:hover {
color:#8EE000 !important; background:rgba(6,19,30,0.9) !important;
}
div[id*='closecontainer'] div[onclick*="closeframe"]:hover {
color:#FF3101 !important; background:rgba(6,19,30,0.9) !important;
}

/* mapper panel */
div#zaqwer_overview {
position:absolute !important;
right:0px !important;
width:229px !important;
height:70% !important;
margin-top:-10px !important;
padding-top:46px !important;
overflow:hidden !important;
background:url(http://gramajda.hekko24.pl/p/mapperdata.png) no-repeat left top !important;
