}
div#zaqwer_overview table.messagestyle {
width:229px !important;
padding:0px 0px 18px 0px !important;
border:0px !important;
border-spacing:0px !important;
background:url(http://gramajda.hekko24.pl/p/pbottom.png) no-repeat 7px bottom transparent !important;
}
div#zaqwer_overview table.messagestyle tbody {
width:200px !important;
padding:3px 8px 3px 12px !important;
display:block !important;
background:url(http://gramajda.hekko24.pl/p/panel.png) repeat-y left top !important;
