}

div#zaqwer_overview table.messagestyle tr[style*="background-color:#003040"] {
background:transparent !important;
}
div#zaqwer_overview table.messagestyle tr[style*="background-color:#003040"] td[colspan="4"]:first-child,
div#zaqwer_overview table.messagestyle tr[style*="background-color:#003040"] td[colspan="1"]:first-child {
