padding-right:8px;
color: var(--text-color);
}
div#zaqwer_overview table.messagestyle tr th {
padding:0px !important;
font-size:14px !important;
background:#32645E !important;
}
div#zaqwer_overview table.messagestyle tr.header th {
padding:0px !important;
font-size:9px !important;
font-variant:normal !important;
font-weight:normal !important;
background:#32645E !important;
color:black !important;
width:0px !important;
overflow:hidden;
white-space:normal !important;
border-top:1px solid #07366B !important;
}
div#zaqwer_overview table.messagestyle tr.normal td {
background-color:#081424 !important;
font-size:11px !important;
width:0px !important;
vertical-align:text-top !important;
}
div#zaqwer_overview table.messagestyle tr.normal td:first-child img {
position:absolute;
margin-left:-2px;
width:14px !important;
height:14px !important;
}
div#zaqwer_overview table.messagestyle tr.normal td:first-child+td font strong {
position:absolute;
margin-left:-5px;
display:block;
width:23px;
overflow:hidden;
white-space:nowrap;
font-weight:normal;
}
div#zaqwer_overview table.messagestyle tr.normal td:first-child+td+td+td font strong {
white-space:nowrap;
font-weight:normal;
}
div#zaqwer_overview table.messagestyle tr td hr {
padding:0px !important;
margin:0px !important;
height:0px !important;
border-bottom:1px solid #07366B !important;
}

/* Clock */
#pardusNavClock {
    position: absolute;
    left:50% ;
    width: 400px;
    margin-left: -200px;
    bottom: 1px;
    line-height: 11px;
    color: inherit !important;
    z-index: 1;
}
#pardusNavClock td { 
    background: none !important;
}
#pardusNavClock thead td { 
    font-size: 9px;
    opacity: .3;
    transition: opacity .3s;
}
#pardusNavClock tbody span { 
    font-size: 13px !important;
    opacity: .3;
    transition: opacity .3s;
}
#pardusNavClock thead td[style*="yellow"],
#pardusNavClock tbody span[style*="yellow"] {
    opacity: .5;
}
#pardusNavClock thead td[style*="lime"],
#pardusNavClock tbody span[style*="lime"],
#pardusNavClock thead td[style*="red"],
#pardusNavClock tbody span[style*="red"],
#pardusNavClock:hover thead td,
#pardusNavClock:hover tbody span {
    opacity: 1;
}


/* Advanced Combat Buttons */
@keyframes emerge {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}
#advanced_combat_buttons_wrapper {
    margin-top: 32px;
	animation: emerge .1s linear;
    z-index: 1;
}
#advanced_combat_buttons_wrapper button {
    position: relative;
    margin: -1px 0;
    border-radius: 50%;
    background: transparent !important;
    border: 0px solid transparent;
    color: #000 !important;
    font-weight: bold !important;
    font-size: 14px !important;
    box-shadow: none !important;
    text-shadow: 0 0px 2px rgba(255,255,255,.2) !important;
    font-family: "trebuchet MS", sans-serif !important;
}
#advanced_combat_buttons_wrapper .enabled {
    cursor: default;
    pointer-events: none;
}
#advanced_combat_buttons_wrapper #btn_adv_combat_BA {
    left: -22px;
}
#advanced_combat_buttons_wrapper #btn_adv_combat_BA:before {
    transform: rotate(90deg);
    left: -3px;
    right: -6px;
}
#advanced_combat_buttons_wrapper button:not(.enabled):hover:before {
    filter: contrast(1.5) brightness(1.3);   
}
#advanced_combat_buttons_wrapper button:before {
    filter: contrast(1);
    transition: all .2s;
    content: '';
    position: absolute;
    top: -6px;
    bottom: -6px;
    left: -6px;
    right: -3px;
    z-index: -1;
    transform: rotate(-90deg);
    background-size: cover;
    background-position: center center;
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="1 1 14 13"><path d="M6.64 2c-.71 0-1.37.38-1.72 1L2.27 7.6a1.99 1.99 0 000 1.98L3.67 12c.35.62 1 1 1.71 1h5.24c.7 0 1.37-.38 1.72-1l1.4-2.42c.35-.61.35-1.37 0-1.99L11.08 3c-.35-.62-1-1-1.72-1z" fill="rgb(39, 118, 195)" /></svg>');
}
#advanced_combat_buttons_wrapper #btn_adv_combat_DC.enabled:before {
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="1 1 14 13"><path d="M6.64 2c-.71 0-1.37.38-1.72 1L2.27 7.6a1.99 1.99 0 000 1.98L3.67 12c.35.62 1 1 1.71 1h5.24c.7 0 1.37-.38 1.72-1l1.4-2.42c.35-.61.35-1.37 0-1.99L11.08 3c-.35-.62-1-1-1.72-1z" fill="rgb(68, 144, 68)" /></svg>');
}
#advanced_combat_buttons_wrapper #btn_adv_combat_BA.enabled:before {
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="1 1 14 13"><path d="M6.64 2c-.71 0-1.37.38-1.72 1L2.27 7.6a1.99 1.99 0 000 1.98L3.67 12c.35.62 1 1 1.71 1h5.24c.7 0 1.37-.38 1.72-1l1.4-2.42c.35-.61.35-1.37 0-1.99L11.08 3c-.35-.62-1-1-1.72-1z" fill="rgb(171, 223, 216)" /></svg>');
}
#advanced_combat_buttons_wrapper #btn_adv_combat_OC.enabled:before {
    background-image: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="1 1 14 13"><path d="M6.64 2c-.71 0-1.37.38-1.72 1L2.27 7.6a1.99 1.99 0 000 1.98L3.67 12c.35.62 1 1 1.71 1h5.24c.7 0 1.37-.38 1.72-1l1.4-2.42c.35-.61.35-1.37 0-1.99L11.08 3c-.35-.62-1-1-1.72-1z" fill="rgb(218, 68, 83)" /></svg>');
