css:
@-moz-document domain("glassdoor.com") {
div, html, body {
    background: #262626;
    color: #808080;
}
div.pageContentWrapper {
    position: relative;
    margin: 0 auto;
    width: 100%;
    background: #262626;;
}

header#TopNav .container-menu {
    -webkit-box-align: center;
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: center;
    background: #262626;
    -webkit-box-orient: horizontal;
    -webkit-box-direction: normal;
    -webkit-flex-direction: row;
    -ms-flex-direction: row;
    flex-direction: row;
    margin-right: 16px;
    overflow: inherit;
    position: inherit;
    width: 100%;
    height: auto;
    -webkit-transform: none;
    -ms-transform: none;
    -o-transform: none;
    transform: none;
    -webkit-transition: none;
    -o-transition: none;
    transition: none;
}
header#TopNav nav {
    background-color: #262626;
    height: 48px;
    -webkit-box-orient: horizontal;
    -webkit-box-direction: normal;
    -webkit-flex-direction: row;
    -ms-flex-direction: row;
    flex-direction: row;
    -webkit-box-pack: start;
    -webkit-justify-content: flex-start;
    -ms-flex-pack: start;
    justify-content: flex-start;
}
li {
    background: #262626;
    color: #808080;
}
li, a:link, a:after {
    color: #808080;
}
header#TopNav .search-bar {
    background: #262626;
    margin: 0 auto;
    padding: 12px 0;
    width: 100%;
    -webkit-box-shadow: 0 1px 1px 1px rgba(127,127,127,0.3);
    box-shadow: 0 1px 1px 1px rgba(127,127,127,0.3);
}
#MainColSummary #jobTitle, #MainColSummary .jobsCount, #MainColSummary .cityOverviewLink {
    font-size: 15px;
    line-height: 22px;
    margin: 12px;
    color: #808080;
}
.jlGrid .jl.selected {
    background-color: #808080;
}
#HzFiltersWrap {
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-pack: justify;
    -webkit-justify-content: space-between;
    -ms-flex-pack: justify;
    justify-content: space-between;
    background-color: #262626;
}
h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
    color: #808080 !important;
}
#JobDescriptionContainer .jobDesc * {
    line-height: 1.4em;
    color: #808080;
}
.jobDetailsInfoWrap .jobDetailsHeader ul.pageTabs {
    background-color: #262626;
    border-bottom: 1px solid #808080;
    border-top: 1px solid #808080;
}
#MainCol, #JDCol {
    overflow-y: scroll;
    overflow-x: hidden;
    height: 100%;
    background-color: #262626;
}
.jobDetailsInfoWrap .tabSection {
    position: relative;
    margin-bottom: 16px;
    background: #262626;
}
#HeroHeaderModule .empWrapper.sticky {
    position: fixed;
    background-color: #262626;
}
.jobDetailsFooter {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    padding: 0 16px;
    background-color: #262626;
    display: -ms-flexbox;
    display: -webkit-box;
    display: -webkit-flex;
    display: flex;
    -ms-flex-pack: justify;
    -webkit-box-pack: justify;
    -webkit-justify-content: space-between;
    justify-content: space-between;
}
.pageTabs li {
    background-color: #262626;
    margin: 0;
    position: relative;
}
.pageTabs li.active, li.hover {
    background-color: #808080;
    border-right-color: #c2c2c2;
}
header#TopNav .container-menu ul[class^='menu-']>li a, header#TopNav .container-menu ul[class*=' menu-']>li a {
    color: #808080;
}
.module {
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    margin: 0 0 16px 0;
    padding: 20px;
    background-color: #161616;
    border-radius: 2px;
    -webkit-box-shadow: 0 1px 1px 1px rgba(127,127,127,0.3);
    box-shadow: 0 1px 1px 1px rgba(127,127,127,0.3);
}
.deckContainer {
    padding: 8px 0;
    background-color: #262626;
}
.jobsBoxesWrap {
    background-color: #262626;
}
ul.jobsBoxes li a .jobDesc .info .title {
    color: lightblue;
    font-size: 16px;
    overflow: hidden;
}
}
