css:
@-moz-document domain("okjike.com")
{
  body
  {
    background: #0F131A;
  }

  .feed-root .feed-nav-container
  {
    background: #0F131A;
  }

  .feed-root .feed-nav
  {
    background: none;
    color: #c8cacf;
  }


/*导航*/
  .header-item
  {
    color: #E0E0E0;
  }

  .site-header
  {
    background: #20252c;
  }

  .site-header
  {
    box-shadow: 0 1px 4px 0 hsla(205, 28%, 2%, 0.5);
  }

  .header-item-active
  {
    background: #E0E0E0;
    color: #2E2F30;
  }

  .site-header .brand-name
  {
    color: #E0E0E0;
  }

  .site-header .brand-beta
  {
    color: #cbbf60;
  }

  .feed-root .feed-nav-tip
  {
    border-radius: 10px;
    border: none;
  }

  .feed-root .feed-nav-item a.active
  {
    color: #E0E0E0;
  }
  .symbol
  {
    color: #333333;
  }

  .popover-inner
  {
    background: #20252c;
  }

  .popover-arrow, .popover-arrow:before
  {
    background: #20252c;
  }
  .popover-arrow, .popover-arrow:after
  {
    background: #20252c;
  }

  .header-notification
  {
    color: #fafafa
  }


/*发布框*/

  .post-creator-body
  {
    background: #1C2129;
  }
  .post-creator .content-wrap
  {
    background: #292F39;
    box-shadow: none;
  }

  .btn-primary.btn-disabled
  {
    background: #0F131A;
  }

  .btn-primary
  {
    background: #ffe400;
  }
  .post-creator .content-wrap .input
  {
    color: #E0E0E0;
  }
  .btn-link .symbol
  {
    color: #b0b2b4;
  }


/*消息框*/

  .feed-root .feed-nav-tip
  {
    background-color: #20252c;
    border-radius: inherit;
  }

  .feed-root .feed-nav
  {
    background-color: #20252c;
  }
  *, :after, :before
  {
    color:#b0b2b4;
  }

/*卡片*/

  .message-card-container
  {
    background: #2E2F30;
  }

  .message-card
  {
    background: #1c2129;
    border-radius: 5px;
  }

  .message-card-header-main .row span
  {
    color: #bbbfc6;
  }

  .message-card-header-main .topic-name
  {
    color: #E0E0E0;
  }

  .readable-content-collapse
  {
    color: #b0b2b4;
  }

  .message-card-topic-img
  {
    border-radius: 50px;
  }

  .message-link
  {
    background: #292F39;
  }

  .message-link:hover
  {
    background: #2a2f39;
  }

  .user-activity-post-link
  {
    background: #2E2F30;
  }

  .readable-content-collapse span
  {
    color: #fff;
  }

  .comments-timeline
  {
    background: #2f3541;
  }

  .comments-timeline .comment-card-header span
  {
    color: #E0E0E0;
  }

  .comments-timeline-container-title,.comments-timeline-more is-flex middle-xs center-xs
  {
    color: #fff;
  }

  .comment-card-main
  {
    border-bottom: #445763 1px dashed;
  }

  .btn-link
  {
    color: #b0b2b4;
  }

  .comments-timeline, .comments-timeline-container
  {
    border-top: 1px solid #445763;
  }

  .sub-comment-timeline
  {
    background: #272c35;

  }

/*悬停卡片*/
  .popover-content
  {
    box-shadow: none;
  }
  .popover-arrow:before
  {
    background: #20252c;
  }
  .popover-arrow:after
  {
    background: #20252c;
    border: 1px solid 
  }

  .popover-inner
  {
    background: #20252c;
    border: 1px solid;
  }

  .topic-card-thumbnail
  {
    border-radius: 50px;
  }

  .topic-subscribe-danger, .topic-subscribe-default
  {
    background: #E0E0E0;
  }

/*推荐关注*/
  .topic-list
  {
    background: #1c2129;
  }

  .topic-list-title
  {
    color: #E0E0E0;
  }

  .topic-card
  {
    background: none;
  }

  .topic-card-title
  {
    color: #bbbfc6;
  }

  .topic-card-title:hover
  {
    color: #E0E0E0!important;
  }

  .topic-list .topic-card-thumbnail
  {
    border-radius: 40px;
  }

/*用户名*/
  .message-card-header-main .topic-extra .message-user a
  {
    color: #528541;
  }

/*其他卡片框*/
  .user-activity
  {
    background: #1a212a;
  }

  .user-card
  {
    background: #1a212a;
  }

/*左侧边栏*/
  .user-profile-main
  {
    background: #1c2129;
    color: #bbbfc6;
  }

  .user-profile
  {
    background: #1c2129;
  }

  .menu
  {
    background: #1c2129;
    color: #bbbfc6;
  }

  .menu-item>a
  {
    color: #bbbfc6;
  }

  .menu-item>a:hover
  {
    background: #528541;
    color: #bbbfc6;
  }
}
