/* ==UserStyle==
@name           kubernetes.io - Dark
@namespace      github.com/openstyles/stylus
@version        1.0.1
@description    Dark theme for kubernetes.io
@author         ivaks
==/UserStyle== */
@-moz-document domain("kubernetes.io") {
* {
  /*     box-sizing: border-box; */
}

header,
footer,
html,
body,
section,
main,
h1,
h2,
h3,
h4,
h5,
h6,
p,
.cards,
#aboutWrapper,
.aboutsection,
#oceanNodes,
#features,
#mainNav,
#encyclopedia,
#docsContent,
#docsToc,
#pre-footer,
.title,
.container-fluid {
  color: #bbb !important;
  background-color: #222 !important;
}

#editPageButton {
  background-color: #555 !important;
  filter: brightness(0.5);
  transform: scale(0.75) !important;
}

p,
h1,
h1,
h3,
h4,
h5,
h6,
li {
  line-height: normal !important;
  font-weight: normal !important;
}

.gridPage p {
  color: #bbb !important;
}

#encyclopedia p,
#page-content-wrapper p {
  text-align: justify !important;
  text-justify: inter-word !important;
}

#encyclopedia {
  padding: 25px !important;
  margin: 0 !important;
}

@media only screen and (min-width: 1024px) {
  #docsToc {
    width: 25% !important;
  }

  #docsContent {
    width: 75% !important;
  }
}

@media only screen and (min-width: 1360px) {
  #docsToc {
    width: 330px !important;
  }

  #docsContent {
    width: 900px !important;
    float: left !important;
  }
}

#docsToc {
  padding: 0 !important;
  margin: 0 !important;
}

#docsContent {
  padding: 0 !important;
  margin: 0 !important;
}

#docsToc .pi-accordion .container .item .title {
  padding: 3px 15px !important;
}

.flyout-button {
  filter: brightness(3) !important;
}

header {
  position: relative !important;
}

#hero {
  padding-top: 10px !important;
}

.issue-button-container > p > a {
  display: none !important;
}

#feature-state-dialog-link {
  color: #ccc !important;
  background-color: #333 !important;
  border-color: #444 !important;
}

input {
  color: #ccc !important;
  background-color: #222 !important;
}

.page-item.active > a {
  font-weight: bold !important;
  color: #5679e6 !important;
  background-color: #444 !important;
}

.panel,
.panel-heading,
.panel-collapse,
.panel-body,
.list-group,
.list-group-item,
.pagination,
.page-link,
.page-item.disabled > span {
  color: #ccc !important;
  background-color: #222 !important;
  border-color: #444 !important;
  border-bottom-color: #444 !important;
}

.item.yah .title {
  color: #fff !important;
  background-color: #333 !important;
}

a {
  color: #6584e4 !important;
  font-weight: normal !important;
}

a.button {
  color: #ccc !important;
}

#markdown-toc li a {
  font-weight: normal !important;
}

.ui-tabs,
.ui-tabs-tab {
  color: #ccc !important;
  background-color: #222 !important;
}

.ui-state-active {
  background-color: #333 !important;
}

.ui-tabs-anchor {
  color: #ccc !important;
}

#encyclopedia .highlight,
#encyclopedia pre,
#encyclopedia code,
#encyclopedia blockquote,
#encyclopedia blockquote > p {
  color: #aaa !important;
  background-color: #333 !important;
  text-decoration: none !important;
  font-weight: normal !important;
  font-family: 'Roboto Mono', monospace !important;
}

#page-content-wrapper p code,
#page-content-wrapper pre,
#page-content-wrapper blockquote,
#page-content-wrapper blockquote > p {
  color: #1c1c1c !important;
  background-color: #aaa !important;
  text-decoration: none !important;
  font-weight: normal !important;
  font-family: 'Roboto Mono', monospace !important;
}

img {
  background-color: #555 !important;
}

.includecode {
  border-color: #444 !important;
}

.includecode thead tr th {
  background-color: #444 !important;
}

#docsContent .includecode thead tr th a code {
  color: #999 !important;
}

#sidebar-wrapper {
  color: #ccc !important;
  background-color: #222 !important;
}
/*   table, thead, tbody, tr, th, td {
    color: #ccc !important;
    background-color: #222 !important;
  } */
thead > tr > th {
  color: #ccc !important;
  background-color: #333 !important;
  border-bottom: 1px solid #444 !important;
}

thead > tr > td {
  color: #ccc !important;
  background-color: #333 !important;
  border-bottom: 1px solid #444 !important;
}


#page-content-wrapper > table > tbody > tr:nth-child(even) > td {
  color: #ccc !important;
  background-color: #2a2a2a !important;
  border-bottom: 1px solid #222 !important;
}

#page-content-wrapper > table > tbody > tr:nth-child(odd) > td {
  color: #ccc !important;
  background-color: #2222 !important;
  border-bottom: 1px solid #222 !important;
}
}
