css:
@-moz-document url-prefix("http://nexxuschat.com/rpg/thekeep/"), url-prefix("http://www.nexxuschat.com/rpg/thekeep/") {
/* MAIN CHAT THEME */
 html, body.mainframe {
    height: 100%;
    margin: 0;
}
html body.mainframe {
    background: #cccccc;
    font-size: 14px;
}
html body.mainframe a, a:link, a:active, a:visited {
    color: #000000;
    -o-transition:.5s;
    -ms-transition:.5s;
    -moz-transition:.5s;
    -webkit-transition:.5s;
    transition:.5s;
}
html body.mainframe a:hover {
    color: #7F7F7F;
}
html body.mainframe p.msg b {
    font-weight: 600;
}
html body.frame {
    background: #555 url(https://images2.imgbox.com/36/c7/YYHoPgdr_o.gif) repeat;
    color: #EFEFEF;
    font-size: 11px;
}
html body.frame .small {
    color: #AAA;
}
html body.frame .parent {
    font-size: 12px;
}
html body.frame div.parent a img {
    display: none;
    visibility: hidden;
}
body > center:nth-child(1) > a:nth-child(4) > img:nth-child(1) {
    display: none;
    visibility: hidden;
}
body > b:nth-child(3) {
    color: #EFEFEF;
    font-size: 12px;
    font-weight: 600;
    font-family: helvetica, arial, geneva, sans-serif;
}
html body.frame a, a:link, a:active, a:visited {
    color: #EFEFEF;
    text-decoration: none;
    -o-transition:.5s;
    -ms-transition:.5s;
    -moz-transition:.5s;
    -webkit-transition:.5s;
    transition:.5s;
}
html body.frame a:hover {
    color: #CFCFCF;
}
html body.frame div a.user img {
    width: 12px;
    height: 12px;
    opacity: .5;
    transition: opacity .5s ease-out;
    -moz-transition: opacity .5s ease-out;
    -webkit-transition: opacity .5s ease-out;
    -o-transition: opacity .5s ease-out;
}
html body.frame div a.user img:hover {
    opacity: 1;
}
html body.frame input {
    background-color: #151515;
    border: 1px solid #666;
    padding: 5px;
    margin-bottom: -10px;
}
body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(1) > input:nth-child(19) {
    padding-left: 8px;
    padding-right: 8px;
    font-weight: bold;
    text-transform: uppercase;
    cursor: pointer;
    -o-transition:.5s;
    -ms-transition:.5s;
    -moz-transition:.5s;
    -webkit-transition:.5s;
    transition:.5s;
}
body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(1) > input:nth-child(19):hover {
	background-color: #222;
}
body > table:nth-child(1) {
    width: 100%;
}
body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(1) > input:nth-child(16) {
    width: 95%;
}
body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(2) {
    float: right;
}
body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) {
    float: right;
    margin-right: 25px;
    padding-left: 10px;
}
body > center:nth-child(1) > a:nth-child(1) > font:nth-child(1) {
    visibility: hidden;
    display: none;
}
.time {
    color: #858585;
    font-size: xx-small;
    -o-transition:.5s;
    -ms-transition:.5s;
    -moz-transition:.5s;
    -webkit-transition:.5s;
    transition:.5s;    
}
.time:hover {
	color: #000;
}
.notify {
    color: #555;
    -o-transition:.5s;
    -ms-transition:.5s;
    -moz-transition:.5s;
    -webkit-transition:.5s;
    transition:.5s;  
}
.notify:hover {
	color: #000;
}
html body.mainframe p.msg b {
    color: #555;
    -o-transition:.5s;
    -ms-transition:.5s;
    -moz-transition:.5s;
    -webkit-transition:.5s;
    transition:.5s;
}
html body.mainframe p.msg b:hover {
 	color: #000;   
}
html body.mainframe p.msg b font {
    color: #555;
    -o-transition:.5s;
    -ms-transition:.5s;
    -moz-transition:.5s;
    -webkit-transition:.5s;
    transition:.5s;
}
html body.mainframe p.msg b font:hover {
	color: #000;   
}
/* PROFILE THEME */
 .title {
    color: #EFEFEF;
}
.whois, html body.frame center table tbody tr td {
    color: #EFEFEF;
}
}
