css:
@-moz-document url-prefix("https://docs.spring.io") {
body {
    background-color: #191919 !important;
    background-image: url('data:image/png;base64,') !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: scroll !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
}
.admonitionblock > table td.content {
    padding-left: 1.125em;
    padding-right: 1.25em;
    border-left: 1px solid #ddddd8;
    color: rgb(213, 210, 205);
}

pre, body, p, .title, h1, h2, h3, .pln, .details {
    color: rgb(213, 210, 205) !important;
}

.hl-tag {
    color: rgb(162, 209, 209) !important;
}

.hl-keyword {
    font-weight: 700 !important;
}

table tr th, table tr td {
    color: rgb(213, 210, 205) !important;
}

table td, table tbody tr {
    border: 0 solid #dedede !important;
}

table thead {
    background: rgb(25, 26, 30) !important;
}

table tr {
    border-width: 1px !important;
    background-color: rgb(25, 26, 30) !important;
}

.sidebar p.title {
    color: rgb(206, 41, 16) !important;
}

.hl-string, .hl-attribute, .hl-value, .typ, .atn, .atv, .str {
    color: #076aff !important;
}

.hl-tag, .hl-keyword, .hl-directive, .kwd, .tag {
    color: #3f7f7f !important;
}

pre.programlisting, pre.screen, .sidebar, #toc.toc2 {
    background-color: rgb(25, 26, 30);
    border-color: rgb(61, 61, 61);
}

div.important code, div.note code, div.tip code, div.warning code {
    background-color: rgb(27, 28, 32) !important;
    border: none !important;
    padding: 1px 3px 0 !important;
    text-shadow: none !important;
    white-space: nowrap !important;
}

:not(pre) > code {
    background-color: rgb(27, 28, 32);
    padding: 1px 3px 0;
    text-shadow: none;
    white-space: nowrap;
}

code {
    font-size: 16px;
    font-family: Consolas,liberation mono,Courier,monospace;
    border: none !important;
    color: rgb(206, 41, 16) !important;
}

.literalblock pre, .listingblock pre:not(.highlight), .listingblock pre[class="highlight"], .listingblock pre[class^="highlight "], .listingblock pre.CodeRay, .listingblock pre.prettyprint {
    background-color: rgb(25, 27, 30) !important;
    background-image: none;
}
}
