css:
@-moz-document domain("nitrotype.com") {
.app-header .navigation li a 
  {
    position: relative;
    display: inline-block;
    padding: 12px 8px;
    font-family: Orbitron,sans-serif;
    font-size: 14px;
    font-weight: 700;
    color: #000000;
  }

  .page-stats .stats-left table.stats-speed th
  {
    font-family: "Droid Serif";
    font-size: 15px;
    color: #ffff;
    line-height: 1;
  }

  .page-stats .stats-left table.stats-speed td
  {
    color: #974890;
    font-size: 22px;
    font-weight: 700;
  }

  .page-stats .stats-left table.stats-medals td
  {
    height: 78px;
    padding: 13px 0 0 52px;
    color: #974890;
    font-size: 16px;
    font-weight: 700;
    vertical-align: middle;
    line-height: 1;
  }

  .page-stats .stats-left table.stats-races td
  {
  color: #974890;
  }

  .page-stats .stats-left table.stats-races th
  {
    color: #ffff;
    width: 40%;
  }

  .overall-stats td, .overall-stats th, .team-stats td, .team-stats th
  {
    font-size: 20px;
    text-align: left;
    color: #fff;
    padding: 15px 10px;
    border-bottom: 1px solid #974890;
  }

  .overall-stats .stat-item, .team-stats .stat-item
  {
    color: #ffffff;
    font-size: 25px;
  }

  .section-header
  {
  position: relative;
    font-family: Orbitron,sans-serif;
    font-size: 24px;
    padding-left: 47px;
    margin-bottom: 10px;
    font-weight: 700;
    color: #974890;
    height: 26px;
    max-width: 560px;
    text-transform: uppercase;
    line-height: 38px;
  }

  .page-stats .stats-right .racer-stats table th
  {
  width: 33.3%;
    padding-bottom: 10px;
    text-align: left;
    font-size: 14px;
}

body, li, p, td, th {
    font-family: "Droid serif";
    font-size: 13px;
    color: #fff;
    line-height: 1;
  }

  .page-stats .stats-right .racer-stats table .member-since
  {
  color: #974890;
  }

  .page-stats .stats-right .racer-stats table .money
  {
  color: #974890;
  }

  .page-stats .stats-right .typing-stats .graph-options a.active, .page-stats .stats-right .typing-stats .graph-options a:active, .page-stats .stats-right .typing-stats .graph-options a:hover
  {
  color: #974890;
  }

  .app-header .navigation li .navigation-jewel
  {
    display: none;
    z-index: 11;
    position: absolute;
    right: -10px;
    top: -5px;
    height: 20px;
    min-width: 30px;
    border-radius: 10px;
    text-align: center;
    padding-top: 3px;
    font-size: 12px;
    color: #fff;
    border: 1px solid #fff;
    background: #974890;
    filter: none;
    background: #974890;
    background: -moz-linear-gradient(top,#f48c91 0,#d40c0b 100%);
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0,#f48c91),color-stop(100%,#d40c0b));
    background: -webkit-linear-gradient(top,#f48c91 0,#d40c0b 100%);
    background: -o-linear-gradient(top,#f48c91 0,#d40c0b 100%);
    background: -ms-linear-gradient(top,#f48c91 0,#d40c0b 100%);
    background: linear-gradient(to bottom,#f48c91 0,#974890 100%);
    box-shadow: 1px 1px 1px rgba(0,0,0,.7);
    line-height: 1;
  } 

  .page-dealership .current-car .car-info td
  {
  font-size: 18px;
  color: #ffff;
  }

  .page-dealership .car-list h2
  {
    clear: left;
    margin: 30px 0 5px;
    text-align: center;
    font-size: 24px;
    font-family: Orbitron,sans-serif;
    font-weight: 700;
    color: #974890;
    line-height: 1;
  }

  .page-dealership .car-list .car .car-info .name
  {
    font-size: 18px;
    color: #ffff;
    margin-bottom: 5px;
  }

  .page-dealership .car-list h3
  {
  text-align: center;
  color: #ffff;
  margin-bottom: 15px;
  }

  .page-dealership .car-list .car .car-info .name
  {
    font-size: 18px;
    color: #ffff;
    margin-bottom: 5px;
  }

  .page-dealership .car-list .car .car-info .price
  {
    font-size: 25px;
    color: #974890;
  }

  .button.button-green
  {
    border: 1px solid #974890;
    background: #974890;
    filter: none;
    background: #974890;
    background: -moz-linear-gradient(top,#387835 0,#ffff 100%);
    background: -webkit-gradient(linear,left top,left bottom,color-stop(0,#387835),color-stop(100%,#3e843a));
    background: -webkit-linear-gradient(top,#387835 0,#3e843a 100%);
    background: -o-linear-gradient(top,#387835 0,#3e843a 100%);
    background: -ms-linear-gradient(top,#387835 0,#3e843a 100%);
    background: linear-gradient(to bottom,#974890 0,974890 100%);
    box-shadow: 0 0 15px 1px rgba(0,0,0,.5) inset, 0 2px 0 #974890, 0 3px 0 3px #974890;
    color: #000;
  }

  .page-dealership .current-car .shop .current-nitros span
  {
  color: #974890;
  }

  .page-dealership .current-car .car-info .cash
  {
  color: #974890;  
  }

  .page-dealership .current-car .car-info th
  {
    color: #ffff;
    text-align: left;
    padding-bottom: 8px;
    font-size: 14px;
  }

  .page-dealership .current-car .car-info td
  {
  font-size: 18px;
  color: #974890;
  }

  .page-dealership .current-car .shop .button
  {
  position: relative;
  display: inline-block;
  padding: 7px 30px 5px;
  background: #a1a1a1;
  filter: none;
  background: #a1a1a1;
  background: -moz-linear-gradient(top,#848484 0,#a1a1a1 100%);
  background: -webkit-gradient(linear,left top,left bottom,color-stop(0,#848484),color-stop(100%,#a1a1a1));
  background: -webkit-linear-gradient(top,#848484 0,#a1a1a1 100%);
  background: -o-linear-gradient(top,#848484 0,#a1a1a1 100%);
  background: -ms-linear-gradient(top,#848484 0,#a1a1a1 100%);
  background: linear-gradient(to bottom,#974890 0,#974890 100%);
  font-size: 13px;
  font-family: Orbitron,sans-serif;
  color: #000;
  cursor: pointer;
  text-align: center;
  text-decoration: none;
  border-radius: 5px;
  border: 1px solid #974890;
  box-shadow: 0 1px 8px 0 rgba(0,0,0,.5) inset, 0 2px 0 2px #974890, 0 3px 0 3px #974890;
  -webkit-transition: color .5s ease;
  }

  .section-header
  {
  position: relative;
  font-family: Orbitron,sans-serif;
  font-size: 24px;
  padding-left: 47px;
  margin-bottom: 10px;
  font-weight: 700;
  color: #fff;
  height: 26px;
  background: url(https://i.imgur.com/2hP9sq7.png) repeat-x 0 0;
  max-width: 560px;
  text-transform: uppercase;
  line-height: 38px;
  }

  .section-header
  {
  position: relative;
  font-family: Orbitron,sans-serif;
  font-size: 24px;
  padding-left: 47px;
  margin-bottom: 10px;
  font-weight: 700;
  color: #fff;
  height: 36px;
  background: url(https://i.imgur.com/2hP9sq7.png) repeat-x 0 0;
  max-width: 560px;
  text-transform: uppercase;
  line-height: 38px;
  }

  .app-header .navigation li a:active, .app-header .navigation li a:hover, .app-header .navigation li.active a
  {
  background: #bf0105;
  filter: none;
  background: #bf0105;
  background: -moz-linear-gradient(top,#fc0009 0,#980202 100%);
  background: -webkit-gradient(linear,left top,left bottom,color-stop(0,#fc0009),color-stop(100%,#980202));
  background: -webkit-linear-gradient(top,#fc0009 0,#980202 100%);
  background: -o-linear-gradient(top,#fc0009 0,#980202 100%);
  background: -ms-linear-gradient(top,#fc0009 0,#980202 100%);
  background: linear-gradient(to bottom,#974890 0,#974890 100%);
  color: #fff;
  border-radius: 5px;
  box-shadow: -3px 2px 0 rgba(0,0,0,.5);
  z-index: 10;
  }

  .app-header .navigation li a:active:after, .app-header .navigation li a:hover:after, .app-header .navigation li.active a:after
  {
  content: "";
  position: absolute;
  width: 45px;
  height: 12px;
  background: url(https://i.imgur.com/2hP9sq7.png) no-repeat 0 0;
  left: 50%;
  margin-left: -23px;
  bottom: -12px;
  }

  .page-garage .racer-info .assets p
  {
  font-size: 16px;
  color: #974890;
  margin-bottom: 15px;
  }

  .page-garage .racer-info .assets p.cash
  {
  color: #974890;
  }

  .page-garage .racer-info .stats .experience span, .page-garage .racer-info .stats .level span
  { 
  color: #974890;
  }
  
  .page-garage .racer-info .stats .experience, .page-garage .racer-info .stats .level
  {
  display: inline-block;
  font-size: 18px;
  color: #ffff;
  margin-bottom: 5px;
  }

  .experience-bar
  {
  padding: 5px;
  font-size: 14px;
  text-align: center;
  border: 1px solid #ffff;
  background: url(https://i.imgur.com/2hP9sq7.png) no-repeat 0 0;
  background-size: 0 auto;
  color: #fff;
  height: 25px;
  }

  .page-garage .racer-info .assets .add-cash
  {
  display: inline-block;
  width: 14px;
  height: 14px;
  background: linear-gradient(to bottom,#974890,#974890);
  box-shadow: 0 1px 1px #000;
  color: #fff;
  font-size: 18px;
  line-height: .75;
  text-align: center;
  text-decoration: none;
  border-radius: 3px;  
  }

  .page-garage .racer-info .countdown span
  {
  color: #974890;
  letter-spacing: .05em;
  font-family: Orbitron,sans-serif;
  }

  .button.button-race  
  {
  position: relative;
  padding: 13px 50px 11px 30px;
  box-shadow: 0 0 18px 1px rgba(0,0,0,.5) inset, 0 3px 0 3px #974890, 0 4px 0 4px #974890;
  font-size: 16px;
  }

  .button.button-red  
  {
  border: 1px solid #974890;
  background: #cc0403;
  filter: none;
  background: #cc0403;
  background: -moz-linear-gradient(top,#790609 0,#cc0403 100%);
  background: -webkit-gradient(linear,left top,left bottom,color-stop(0,#790609),color-stop(100%,#cc0403));
  background: -webkit-linear-gradient(top,#790609 0,#cc0403 100%);
  background: -o-linear-gradient(top,#790609 0,#cc0403 100%);
  background: -ms-linear-gradient(top,#790609 0,#cc0403 100%);
  background: linear-gradient(to bottom,#974890 0,#974890 100%);
  box-shadow: 0 0 15px 1px rgba(0,0,0,.5) inset, 0 2px 0 2px #2d0a0b, 0 3px 0 3px #222323;
  color: #ffffff;
  }

  .page-garage .survey-box .dollar-sign
  {
  position: absolute;
  left: 18px;
  top: 7px;
  color: #974890;
  font-size: 70px;
  }
  
  .page-race .friends-race span, .page-race .race-tip span
  {
  color: #974890;
  font-size: 16px;
  line-height: 1.4;
  }

  .race-results .button-area .current-cash .amount
  {
  font-size: 28px;
  color: #974890;
  margin-bottom: 0;
  line-height: 1;
  }

  .race-results .earnings .breakdown-cash
  {
  color: #974890;
  }

  .race-results .earnings .breakdown-bonus
  {
  color: #974890;
  }
  
  .race-results .stats
  {
  color: #974890;
  }
  
  .race-results .racer-names
  {
 color: #974890;
  }
  
  .button
  {
  position: relative;
  display: inline-block;
  padding: 7px 30px 5px;
  background: #a1a1a1;
  filter: none;
  background: #a1a1a1;
  background: -moz-linear-gradient(top,#848484 0,#a1a1a1 100%);
  background: -webkit-gradient(linear,left top,left bottom,color-stop(0,#848484),color-stop(100%,#a1a1a1));
  background: -webkit-linear-gradient(top,#848484 0,#a1a1a1 100%);
  background: -o-linear-gradient(top,#848484 0,#a1a1a1 100%);
  background: -ms-linear-gradient(top,#848484 0,#a1a1a1 100%);
  background: linear-gradient(to bottom,#848484 0,#a1a1a1 100%);
  font-size: 13px;
  font-family: Orbitron,sans-serif;
  color: #ffff;
  cursor: pointer;
  text-align: center;
  text-decoration: none;
  border-radius: 5px;
  background: linear-gradient(to bottom,#974890 0,#974890 100%);
  border: 1px solid #ec9cb0;
  box-shadow: 0 1px 8px 0 rgba(0,0,0,.5) inset, 0 2px 0 2px #974890, 0 3px 0 3px #222323;
  -webkit-transition: color .5s ease;
  -moz-transition: color .5s ease;
  -o-transition: color .5s ease;
  transition: color .5s ease;
  line-height: 1;
  }

  .page-team-view .team-profile .quick-info p span
  {
  color: #974890;
  }

  .page-team-view .num-views span
  {
  color: #974890;
  }
  
  .page-team-view .officer p span
  {
  color: #974890;
  }
}
