css:
@-moz-document domain("my.fogserver.com") {
/*backgrounds & general some text color*/
/*dark-dark*/
body, button, .select2-container--default .select2-selection--single, input, .panel-heading, table, tr, th, td, .btn-info, .dropdown-menu {
    background-color: #292929 !important;
    color: #d5d5d5 !important;
    background-image: none !important;
}
/*light-dark*/
.form-signin, .panel-body, .input-group-addon, textarea, .ui-widget-content {
    background-color: #2f2f2f !important;
    color: #d5d5d5;
    background-image: none;
}

/*text color*/
/*general*/
.select2-selection__rendered {
    color: #d5d5d5 !important;
}
/*highlights*/
b, a, a:hover, select {
    color: #02a3b4 !important;
    background-color: transparent !important;
}
}
