@document domain(javlibrary.com){
body,
body *,
html * {
	font-family: "ÂæÆËΩØÈõÖÈªë", "MSYH", "Microsoft Yahei"
}

body.main {
	background: #FFF !important;
	width: 100%;
	overflow-x: hidden;
	overflow-y: scroll;
}

.videothumblist {
	height: auto !important
}

a:link {
	color: #25C6FC
}

a:visited {
	color: #1F6FB5
}

a:hover,
a:active {
	color: #FF534D;
}

input,
label,
select,
button,
textarea {
	margin: 0;
	padding: 0;
	display: inline-block;
	vertical-align: middle;
	white-space: normal;
	background: none;
	line-height: 1;
	border: 1px solid rgba(0, 0, 0, 0.5);
	background-color: #fff
}

input:focus {
	outline: 0
}

input,
textarea {
	box-sizing: content-box
}

.clear:after {
	display: block;
	content: "";
	height: 0;
	width: 0;
	clear: both;
	float: none
}

#topmenu {
	background: #344047;
	height: auto;
	padding: .3em 0 .2rem 2.5rem;
}

#topmenu:after {
	display: block;
	content: "";
	height: 0;
	width: 0;
	clear: both;
	float: none
}

#topmenu .searchbar {
	margin: 0;
	position: static;
	padding-left: 1em;
	float: left
}

#topmenu .searchbar #idsearchbox {
	font-size: 12px;
	height: auto;
	line-height: 1.5;
	background-image: none;
	box-shadow: none;
	border-radius: .3em
}

#topmenu .searchbar #idsearchboxmask {
	font-size: 12px;
	line-height: 1.35em;
	text-indent: .5em;
	margin: .3em 0 0 4.5em
}

#topmenu .searchbar #idsearchbutton {
	background: #fb5557;
	border: 0;
	content: "Serch";
	padding: .27em 1em;
	font-size: 12px;
	height: auto;
	width: auto;
	color: #fff;
	font-weight: 300;
	margin-left: .75em;
	font-family: "ÂæÆËΩØÈõÖÈªë", "MSYH", "Microsoft Yahei";
	border-radius: .3em;
	box-shadow: none;
	cursor: pointer
}

#topmenu .searchbar #idsearchbutton:active,
#topmenu .searchbar #idsearchbutton:hover {
	position: static
}

#topmenu .searchbar td.advsearch {
	width: auto;
	padding: 0;
	vertical-align: middle;
	color: rgba(255, 255, 255, 0)
}

#topmenu .searchbar td.advsearch a,
#topmenu .searchbar td.advsearch a:hover,
#topmenu .searchbar td.advsearch a:active {
	color: #fff;
	border-radius: .3em;
	margin: 0;
	display: inline-block;
	padding: .1em 1em;
	background: #fb5557;
	font-weight: 300
}

#topmenu .searchbar td.advsearch a:hover,
#topmenu .searchbar td.advsearch a:active {
	background: #fb5557
}

#topmenu .searchbar td.socialmedia {
	display: none
}

#topmenu .socialmedia {
	content: ""
}

#topmenu .menutext {
	line-height: 1.7em;
	position: static;
	float: right;
	margin-top: 0;
	font-size: 12px;
	margin-right: 3rem;
	color: rgba(255, 255, 255, 0.5)
}

#topmenu .menutext a,
#topmenu .menutext a:link,
#topmenu .menutext a:visited {
	color: #fff;
	margin: 0 .5em
}

#topmenu .menutext a:hover,
#topmenu .menutext a:active {
	color: rgba(255, 255, 255, 0.6)
}

#toplogo {
	display: block;
	height: 40px;
	width: auto;
	box-sizing: content-box;
	padding: .5rem 3.5rem;
}

#toplogo .sitelogo,
#topbanner11 {
	height: 40px;
	position: relative;
	top: 0;
	left: 0;
}

#toplogo .sitelogo {
	position: absolute;
	margin: .5rem 0 0 3.5rem;
}

#toplogo img {
	display: block;
	height: 40px !important;
	width: auto !important;
}

#content {
	background: #fff;
	margin: 0;
	padding: 0;
	width: 100%;
	border: 0
}

#leftmenu {
	width: 10em;
	height: 100%;
	position: fixed;
	left: 0;
	top: 0;
	background: #fff;
	border: 0;
	border-right: .2rem solid #fb5557;
	transition: margin-left .3s;
	z-index: 9;
	margin-left: -10em;
	box-shadow: 0 0 1rem rgba(0, 0, 0, .25)
}

#leftmenu:before {
	content: "MENU";
	display: block;
	background: #fb5557;
	color: #FFF;
	text-align: center;
	height: 2rem;
	width: 4rem;
	line-height: 2rem;
	font-size: 1rem;
	position: absolute;
	right: -3rem;
	top: 3rem;
	transform: rotate(90deg);
	transition: all .3s;
}

#leftmenu:hover {
	margin-left: 0;
	transition: all .3s
}

/* #leftmenu:hover:before {
    right: -1rem;
    transition: all .3s;
} */
#leftmenu .menul1 {
	background: #fff;
	margin: 0
}

#leftmenu .category {
	font-size: 14px;
	color: #34495e;
	line-height: 2em;
	background: rgba(149, 165, 165, 0.1);
	border-bottom: 1px solid rgba(255, 255, 255, 0.2)
}

#leftmenu li {
	border: 0
}

#leftmenu li a {
	font-size: 12px;
	background: transparent;
	color: #34495e;
	border: 0;
	border-bottom: 1px solid rgba(255, 255, 255, 0.1);
	text-indent: 1.5em;
	border-left: 0;
	line-height: 2em;
	padding: .25rem;

	transition: all .2s
}

#leftmenu li a:last-child {
	border: 0
}

#leftmenu a:before {
	content: "";
	width: 0;
	height: 0;
	border-bottom: .3rem solid transparent;
	border-top: .3rem solid transparent;
	border-left: .3rem solid rgba(52, 73, 94, 0.2);
	position: absolute;
	margin-top: .4rem;
	margin-left: -.75rem;
	font-size: 0;
	line-height: 0;

	transition: all .2s
}

#leftmenu li a {
	border: 0;
}

#leftmenu li a:hover {
	background: #fb5557;
	color: #FFF;
	border: 0;
	padding-left: 1rem;

	transition: all .2s
}

#leftmenu a:hover:before {
	border-left-color: #FFF;

	transition: all .2s
}

#leftmenu table {
	display: none !important;
}
#rightcolumn {
	padding: 0;
	margin-left: 2rem;
	border: 0
}

#rightcolumn .displaymode {
	box-sizing: border-box;
	padding-left: 15em;
	position: absolute;
	right: 1em;
	margin-top: -1.75em
}

#rightcolumn .displaymode .left {
	font-size: 12px;
	color: #34495e
}

#rightcolumn .displaymode a {
	color: #34495e
}

#rightcolumn .boxtitle {
	position: static;
	font-size: 14px;
	text-indent: 2em;
	color: #34495e;
	display: block;
	padding: .5em 2em;
	background: rgba(0, 0, 0, 0.02);
	width: auto
}

#rightcolumn #video_title {
	font-size: 14px;
	padding: 1em;
	border-bottom: 1px solid #fb5557
}

#rightcolumn #video_title a {
	color: #fb5557
}

#rightcolumn .post-body {
	width: 99%;
	margin: 0 auto
}
#rightcolumn #video_jacket {
	min-width: 20rem;
	padding: .5rem;
}
#rightcolumn #video_jacket img {
	margin: 0 auto;
	border-radius: .5em;
	width: 100%;
	max-width: 60rem;
	display: block
}

#rightcolumn #video_info .item table {
	border: 0
}

#rightcolumn #video_info .item .header {
	width: 6em
}

#rightcolumn #video_info .item .text {
	padding: 0 2em
}

#rightcolumn #video_info .item a {
	color: #fb5557
}

#rightcolumn #video_info .item .icn_favdirector,
#rightcolumn #video_info .item .icn_favmaker,
#rightcolumn #video_info .item .icn_favlabel,
#rightcolumn #video_info .item .icn_favstar {
	background: none;
	margin: 3px;
	-webkit-filter: drop-shadow(0 1px 1px rgba(0, 0, 0, 0.2))
