#rightcolumn #video_info .item .icn_favdirector:before,
#rightcolumn #video_info .item .icn_favmaker:before,
#rightcolumn #video_info .item .icn_favlabel:before,
#rightcolumn #video_info .item .icn_favstar:before {
	font-family: 'fad';
	font-size: 16px;
	color: #fff;
	display: block;
	height: 16px;
	width: 16px;
	padding: 3px;
	text-align: center;
	content: "‚ô•";

	transition: all .2s
}

#rightcolumn #video_info .item .icn_favdirector:hover:before,
#rightcolumn #video_info .item .icn_favmaker:hover:before,
#rightcolumn #video_info .item .icn_favlabel:hover:before,
#rightcolumn #video_info .item .icn_favstar:hover:before {
	color: #e26967;

	transition: all .2s
}

#rightcolumn #video_info .item .genre,
#rightcolumn #video_info .item .cast {
	padding: .3em 1em;
	background: #fb5557;

	transition: all .2s
}

#rightcolumn #video_info .item .genre a,
#rightcolumn #video_info .item .cast a {
	color: #fff;

	transition: all .2s
}

#rightcolumn #video_info .item .genre:hover,
#rightcolumn #video_info .item .cast:hover {
	cursor: pointer;
	padding: .3em 1.5em;

	transition: all .2s;
	background: #fff
}

#rightcolumn #video_info .item .genre:hover a,
#rightcolumn #video_info .item .cast:hover a {
	color: #fb5557;

	transition: all .2s
}

#rightcolumn #video_info .item .genre:hover .icn_favstar:before,
#rightcolumn #video_info .item .cast:hover .icn_favstar:before {
	color: #fb5557
}

#rightcolumn #video_info .item .text .icn_favmaker:before,
#rightcolumn #video_info .item .text .icn_favlabel:before {
	color: #fb5557
}

.videothumblist {
	padding-left: 1em;
	margin: 0;
}

.videothumblist:after {
	display: block;
	content: "";
	height: 0;
	width: 0;
	clear: both;
	float: none
}

.videothumblist .videos {
	display: flex;
	flex-wrap: wrap;
}

.videothumblist .videos .video {
	max-width: 12.5%;
	min-width: 12.5%;
	flex: 1;
	display: block;
	box-sizing: border-box;
	margin: 1em 0;
	padding:0 .5rem;
	border: 0;
	height: auto;
	border-radius: .35rem;
	transition: background .3s;
	overflow: hidden;
}

.videothumblist .videos .video .id {
	display: block;
	position: absolute;
	z-index: 1;
	font-size: .7rem;
	font-weight: 700;
	padding: .2em .4rem;
	background: #fb5557;
	color: #fff;

	transition: all .2s
}

.videothumblist .videos .video .title {
	display: block;
	font-size: 12px;
	line-height: 1.5em;
	height: 3em;
	overflow: hidden;
	color: #34495e;
	position: relative;
	width: 90%;
	margin: .5em 3%;
	border-radius: .3em;
	float: none
}

.videothumblist .videos .video img {
	display: block;
	width: 100%;
	height: auto;
	border-radius: 1.1rem;
	clip-path: inset(.2rem);
}

.videothumblist .videos .video:hover {
	z-index: 2;
	background: rgba(251, 85, 87, .1);
	transition: background .3s;
}

.videothumblist .videos .video:hover .title {
	z-index: 2;
	color: #fb5557;
	transition: background .2s;
}

.videothumblist .videos .toolbar {
	display: none !important
}

.page_selector a.page,
.page_selector span,
.page_selector span.first,
.page_selector span.prev {
	padding: .5rem 1.5em !important;
	font-size: .8rem;
	line-height: 1rem;
	height: 1rem;
	margin: 0 .5rem;
	border: 0;
	background: #fb5557;
	color: #FFF;
}

.page_selector a.page:hover,
.page_selector span:hover,
.page_selector span.first:hover,
.page_selector span.prev:hover,
.page_selector a.page:active,
.page_selector span:active,
.page_selector span.first:active,
.page_selector span.prev:active {
