}

.page_selector a.page.current,
.page_selector span.current,
.page_selector span.first.current,
.page_selector span.prev.current,
.page_selector a.page.current:hover,
.page_selector span.current:hover,
.page_selector span.first.current:hover,
.page_selector span.prev.current:hover {
	background: transparent;
	border: 0;
	color: #fb5557;
}

#video_comments {
	padding-left: 2em
}

hr.grey {
	display: none
}

#video_reviews {
	padding: 1rem;
}

#video_reviews .header {
	font-size: 16px;
	line-height: 2em;
	height: auto;
	padding-left: 2em;
	color: #fb5557;
	border-bottom: 1px solid #fb5557;
