css:
@-moz-document domain(mastodon.social) {
body *:not(.fa) {
    font-family: "GohuFont", monospace !important;
    font-size: 11px !important;
    font-weight: normal !important;
    line-height: 11px !important;
    border-radius: 0 !important;
}
.column-header__buttons {
    height: 11px;
    margin-right: 2px;
}
.columns-area > * {
    padding: 2px;
}
.drawer__header {
    height: 15px;
    padding-top: 2px;
    margin-bottom: 4px;
}
.drawer__tab {
    padding: 0;
    margin: 0;
}
.search__icon .fa {
    height: 11px;
    width: 11px;
    font-size: 11px;
    top: 2px !important;
}
.search {
    margin-bottom: 4px;
}
.search input, .navigation-bar, .compose-form {
    padding: 2px;
}
.compose-form textarea {
    padding-left: 4px !important;
    padding-top: 4px !important;
}
.compose-form__buttons-wrapper {
    padding: 2px !important;
}
.compose-form__buttons {
    display: block !important;
    height: 11px;
}
.compose-form__buttons * {
    padding: 0 !important;
    margin: 0 !important;
    height: 11px !important;
    width: 13px !important;
}
.compose-form__buttons > * {
    display: inline-block;
}
.compose-form__publish-button-wrapper {
    padding: 2px !important;
    padding-right: 0 !important;
}
.compose-form__publish-button-wrapper button {
    padding: 2px !important;
    height: 14px !important;
}
.compose-form__warning, .reply-indicator {
    padding: 2px !important;
    margin-bottom: 2px !important;
}
.reply-indicator__header {
    padding: 2px;
    margin-bottom: 0;
}
.reply-indicator__cancel button {
    height: 11px !important;
    width: 11px !important;
}
.reply-indicator__display-avatar, .reply-indicator__display-avatar div {
    height: 22px !important;
    width: 22px !important;
}
.reply-indicator__content p, .status__content p {
	margin-bottom: 11px;
}
.spoiler-input input {
    padding: 2px !important;
}
.spoiler-input.spoiler-input--visible {
    height: 17px !important;
}
.privacy-dropdown__option {
    padding: 2px;
}
.autosuggest-textarea__suggestions--visible {
    padding: 2px !important;
}
.autosuggest-textarea__suggestions--visible > div {
    padding: 2px !important;
}
.autosuggest-textarea__suggestions--visible .account__avatar {
    width: 18px !important;
    height: 18px !important;
    background-size: 18px !important;
}
h1 button {
    padding: 2px !important;
}
h1 {
    height: 18px;
}
.column-header__wrapper.active::before {
    top: 6px;
}
.status {
    padding: 2px;
    padding-left: 50px;
}
.status__avatar {
    top: 2px;
    left: 2px;
    width: 44px;
    height: 44px;
}
.account__avatar {
    width: 44px !important;
    height: 44px !important;
    background-size: 44px 44px !important;
}
.status__prepend {
    padding: 2px;
    margin-left: 61px;
}
.status__prepend-icon-wrapper {
    left: -13px;
}
.status__action-bar {
    margin-bottom: 4px;
    margin-top: 4px;
}
.status-card:not(.horizontal):not(.interactive) {
    margin-top: 11px;
    height: 44px !important;
}
.status-card:not(.horizontal):not(.interactive) .status-card__image-image {
    height: 44px !important;
}
.status__content__read-more-button {
    padding-top: 11px;
}
.media-gallery {
    margin-top: 4px;
}
.status__action-bar button, .status__action-bar-dropdown {
    height: 11px !important;
    width: 11px !important;
    margin-right: 4px;
}
.status__action-bar__counter {
    margin: 0;
}
.notification__message {
    margin: 0;
    margin-left: 61px;
}
.notification__favourite-icon-wrapper {
    left: -15px;
}
.notification__filter-bar > *{
    padding-top: 6px !important;
    padding-bottom: 6px !important;
}
.notification__filter-bar button.active::after, .notification__filter-bar button.active::before {
    border-width: 0 5px 5px;
}
.dropdown-menu {
    padding: 0;
}
.dropdown-menu__item > a {
    padding: 2px;
}
.dropdown-menu__separator {
    margin: 0;
}
.account__action-bar-links a, .account__action-bar-dropdown {
    padding: 4px;
}
.account__action-bar-dropdown .icon-button {
    width: 22px !important;
    height: 22px !important;
}
.account__section-headline a {
    padding: 6px;
}
.account__section-headline a::before, .account__section-headline a::after {
    border-width: 0 5px 5px !important;
}
.account__header__fields {
    margin:0 !important;
    _margin-top: 11px !important;
    padding: 0 !important;
}
.account__header__fields dt, .account__header__fields dd {
    padding: 6px !important;
}
.account__disclaimer, .column-back-button {
    padding: 2px;
}
.account-gallery__item {
    width: 16.6%;
}
.account__header > div {
    text-align: left;
    padding: 0px;
    padding-top: 15px;
}
.account__header__display-name, .account__header__username, .account__header__content {
    margin-left: 4px;
}
.account__header__content {
    padding-bottom: 11px;
}
.account__header__avatar {
    margin: 0px 3px 11px;
}
.account--action-button {
    top: 5px;
    right: 5px;
    float: right;
    padding: 0 !important;
}
.account--action-button .icon-button {
    width: 150px !important;
    height: 22px !important;
    text-align: right;
    font-size: 22px !important;
}
.account--follows-info {
    padding: 2px;
    padding-right: 23px;
    top: 0px;
    left: 2px;
}
.emojione {
    width: 12px !important;
    height: 11px !important;
    margin: 0 !important;
}
.emoji-picker-dropdown {
    top: 2px !important;
    right: 4px !important;
}
.emoji-button {
    width: auto;
}
.invisible {
    display: none;
}
i.fa-retweet{
	background-image: none !important;
}
}
