css:
@-moz-document domain("screenshots.firefox.com") {
   .header-panel .nav-button img {
	filter: invert(100%) !important;
}
    .app-body {
	background: #000;
}
    .header-panel {
	background-color: #000;
}
    .shot-info {
	color: #fff;
}
    .shot-alt-actions {
	background-color: #000;
}
    .header-panel .nav-button:hover {
	background-color: #181818 !important;
}
    .shot {
	background: #181818;
}
    .shot h4 {
	color: #fff;
}
    .shot:hover, .shot.panel-open {
	box-shadow: 0 0 0 5px #000;
}
    .shot {
	background: #181818;
}
    .shot .alt-actions-container {
	background: #181818;
}
    .button.download {
	filter: invert(100%) !important;
}
    .shot .indicator.link {
	filter: invert(100%) hue-rotate(180deg) !important;
}
    .header {
	color: #fff;
}
    .sub-header {
	color: #fff;
}
    .preferences .title {
	color: #d1d1d1;
}
    .close-preferences {
	filter: invert(100%) !important;
}
    .button, .actions button {
	background-color: #fff;
}
    .preferences hr {
	border-top: 1px solid #5f5f5f;
}
    .preferences .sub-info {
	color: #939393;
}
    .footer .mozilla-logo {
	filter: invert(100%) !important;
}
    .shot {
	box-shadow: 0 2px 8px rgba(255, 255, 255, 0.1);
}
    .button.warning {
	filter: invert(100) !important;
}
    .header-panel .logo .screenshots-logo {
	filter: invert(100%) hue-rotate(180deg) brightness(2) !important;
}
    p{
        color:white;
    }
    section:nth-child(2n) {
	background-color: #000;
}
    .section-content {
	color: #fff;
}
    h2 {
	color: #009ec0;
}
    .banner {
	background-color: #009ec0;
}
.delete-confirmation-dialog {
	filter: invert(100) !important;
}
.share-panel {
	filter: invert(100) !important;
}
.share-panel .share-buttons {
	border-bottom: 1px solid #2d2d2d;
	filter: invert(100) !important;
}
}
@-moz-document url("https://screenshots.firefox.com/shots") {

    .button.transparent {
	background-color: transparent;
	color: #38383d;
	filter: invert(100) !important;
}
}
