css:
@-moz-document url-prefix("https://master.tech-camp.in/") {
body, table, input, textarea, dd, .toggle_label, .header, .main, .curriculum_content-list, .curriculum_layout-list, .navbar-header, .container, .toggle_navigation_banners, .progress_layout-title_lesson, .col-md-9, .col-sm-12, .curriculum-inner, .progress-checker, .progress-checker-buttons, .progress-share, .navigation_banners, .ng-scope, #curriculum form {
    color: #ddd;
    background-color: #444;
    background: #444;
}
#curriculum .curriculum_content {
    background-color: #444;
    background: #444;    
}
.header .inner-list {
    background-color: #777; 
}
.main .container .ng-binding, .text-center, .content-link {
    color: #eee;
}
#curriculum .curriculum_content {
 	box-shadow: 0 1px 5px 3px rgba(230,230,230,0.15);   
}
.curriculum_content-list .list-group-item .ng-binding:hover {
    color: #444;
}
.header {
    box-shadow: 0 1px 2px rgba(230,230,230,0.15);   
}
.header .navbar-nav a, .toc-tree li a {
    color: #eee;
}
.main .container .progress_layout-title_lesson small, .main .container .progress_layout-title_lesson {
    color: #444;
}
#curriculum .progress-checker-buttons, .curriculum-edit-button {
    background-color: #333;
}
#curriculum .progress-share.bg_icon-pattern {
    background-image: none;
}
.toc-tree {
    background-color: #888;
}
#curriculum table tbody>tr:nth-child(odd) {
	background-color: #555;    
}

.main_header>.container ul.navbar-nav>li, .toc-tree li.disabled {
 	background-color: #555;   
}
.main_header>.container .navbar-nav a, .list-group-item.disabled {
    color: #ddd;
}

.main_header>.container .inner-list {
    background-color: #777;
}
.main_header>.container .navbar-nav .bg-info a {
    color: #222;
}
#toggle_navigation_banners .toggle_label, #toggle_navigation_banners .navigation-banner {
    background-color: #333;
}
.curriculum_layout .curriculum_content {
	box-shadow: 0 0px 2px 0 rgba(230,230,230,0.12), 0 2px 2px 0 rgba(230,230,230,0.24);
}
}
