@-moz-document domain("ezgif.com") {
::-webkit-scrollbar {width: 16px; height: 16px;}
::-webkit-scrollbar-button {width: 0px; height: 0px;}
::-webkit-scrollbar-thumb {background: #6e6e6e; border-radius: 4px; border: 2px solid #222;}
::-webkit-scrollbar-thumb:hover {background: #7e7e7e;}
::-webkit-scrollbar-thumb:active {background: #9e9e9e;}
::-webkit-scrollbar-track {background: #222;}
::-webkit-scrollbar-corner {background: #222;}

body {background: #181818; color: #ccc;}

#wrapper {background: #222;}
#logo {filter: invert(1);}

a {color: #5194cf;}
a:focus, a:hover {color: #77abd9;}

#header {border-color: #333;}

#menu a {color: #ccc;}
#menu li.active a {background-color: #333;}
#menu a:hover, #menu a:active, #menu li:hover a {background-color: #333;}
#menu li ul {background: #333;}

#submenu {background: #333;}
#submenu ul li:first-child {border-color: #333;}
#submenu ul li a {border-left-color: #444; border-right-color: #282828; color: #aaa;}
#submenu ul li.active a {background: #222; color: #ccc;}

#submenu img[src="https://static.ezgif.com/images/webp-logo.png"] {filter: invert(1) hue-rotate(180deg) saturate(6) saturate(0.5);}
#submenu img[src="https://static.ezgif.com/images/apng-logo.png"] {filter: invert(1) hue-rotate(180deg) saturate(6);}

h1, h2, h3 {color: #aaa;}
#site-description {color: #999;}
#news li {background-color: #333;}
.tweet_time::after {color: #999;}
.paginator a {border-color: #444;}

span[style="color:#2c882c"], span[style="color:green"] {color: #7cb73c !important;}

#upload-form {background: #181818;}
#upload-form fieldset {background: #222; border-color: #333;}
.form legend {color: #aaa;}
.form input.text, .form textarea, .form select {background: #222; border-color: #333; color: #ccc;}

.file-menu {background: transparent; border-color: transparent;}
.file-menu a {background: #181818; border-color: #333; box-shadow: none; text-shadow: none;}
.file-menu a.active {background: #282828; border-color: #333; color: #ccc;}
.file-menu a:hover, .file-menu a.save:hover {background: #383838; border-color: #555; box-shadow: none; color: #ccc;}
.file-menu a.save {color: #7cb73c;}

.ui-widget-content {background: #111; border-color: #333;}
.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {background: #333; border-color: #444;}
.ui-state-active,.ui-widget-content .ui-state-active,.ui-widget-header .ui-state-active {background: #444; border-color: #555;}

input[type="checkbox"] {-webkit-appearance: none; -moz-appearance: none; appearance: none; background: #222; border: 1px solid #444; border-radius: 2px; height: 13px; width: 13px;}
input[type="checkbox"]:checked {background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="-40 -40 592 592"><path fill="%23ccc" d="M173.898 439.404l-166.4-166.4c-9.997-9.997-9.997-26.206 0-36.204l36.203-36.204c9.997-9.998 26.207-9.998 36.204 0L192 312.69 432.095 72.596c9.997-9.997 26.207-9.997 36.204 0l36.203 36.204c9.997 9.997 9.997 26.206 0 36.204l-294.4 294.401c-9.998 9.997-26.207 9.997-36.204-.001z"/></svg>') no-repeat, #222;}
input[type="checkbox"]:indeterminate {background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="-50 0 448 512"><path fill="%23ccc" d="M416 208H32c-17.67 0-32 14.33-32 32v32c0 17.67 14.33 32 32 32h384c17.67 0 32-14.33 32-32v-32c0-17.67-14.33-32-32-32z"></path></svg>') no-repeat, #222;}

.sub-options:before {border-color: #333 transparent;}
.sub-options:after {border-color: #222 transparent;}
.sub-options {background: #222; border-color: #333;}

#st-frames .f {background: #181818; border-color: #333;}
.txt-opt {background: #111; border-color: #222;}

#animation-frames li {background: #181818;}
.frame-delay, .frame-tools, .form .frame-delay input.text.tiny {color: #aaa;}

p[style="font-size:12px;margin:0;padding:5px;color:#444;max-width:500px"], p[style="font-size:12px;margin:0;padding:5px;color:#444;"] {color: #999 !important;}
p[style="font-size:13px;margin:0;padding:5px;color:#333;max-width:500px"] {color: #aaa !important;}
.options {background: #181818; border-color: #333;}
.h-line {border-color: #333;}
.form input.text.tiny {color: #ccc;}

#output {background: #181818; border-color: #333; color: #aaa;}
.button {text-shadow: none; box-shadow: none;}
.button:hover {color: #ccc;}
.convert-btn {background: #333; border-color: #333; color: #5194cf;}
.convert-options {background: #333;}
.txt {background: #181818;}

.notice {background: #241d00; color: #ffd324;}
.success {background: #1a1f09; border-color: #b7cf5b; color: #b7cf5b;}
.error {background: #260304; border-color: #f79498; color: #f79498;}

#footer {background: #181818; border-color: #222;}
.footer-menu-row strong {color: #9ea7b0;}
span[style="display:inline-block;background:#fff;padding:2px 6px;font-size:90%"] {background: #222 !important;}
}
