css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("sacrarium24.ru") {

::-webkit-scrollbar {
 height: 12px;
 width: 12px;
 background-color: rgba(87, 58, 117, 0);
 
 transition: background-color 200ms linear, width 150ms linear;
 -o-transition: background-color 200ms linear, width 150ms linear;
 }
 ::-webkit-scrollbar:hover {
 height: 12px;
 width: 12px;
 background-color: rgba(0, 0, 0, 0.0);
 -webkit-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.8);
 }
 /* Scroll line */
::-webkit-scrollbar-thumb {
 background-color: rgba(211, 207, 216, 0.5);
 
 -webkit-border-radius: 1ex;
 border: rgba(211, 207, 216, 0.5);
 
 box-shadow: inset 0 0 0 1px rgba(0, 0, 0, 0.7), 0 0 5px rgba(0, 0, 0, 0.8);
 -webkit-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.7);
 -webkit-box-shadow: inset 0 0 0 1px rgba(0, 0, 0, 0.7), 0 0 5px rgba(0, 0, 0, 0.8);
 -moz-box-shadow: inset 0 0 0 1px rgba(0, 0, 0, 0.7), 0 0 5px rgba(0, 0, 0, 0.8);
 transition: background-color 200ms linear, width 150ms linear;-o-transition: background-color 200ms linear, width 150ms linear;}
 ::-webkit-scrollbar-thumb:hover {background-color: rgba(0, 0, 0, 0.8);-webkit-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.8);} 

#fkbx>input, input {font: bold Arial;color: #5d417b;}

/***** Style *****/
body {position: relative;padding-top: 40px;background: #232323;}
body > .navbar .brand {color: #a80101;text-shadow: 0 1px 0 rgba(255,255,255,.1), 0 0 30px rgba(255,255,255,.125);-webkit-transition: all .2s linear;-moz-transition: all .2s linear;transition: all .2s linear;}
h1 {font-size: 38.5px;color: #a80101;text-shadow: 1px 1px;}

#link_3,#link_9,#link_32,#link_8,#link_10,#link_29,#link_28,#link_33 {background: url(http://sacrarium24.ru/forum/images/MyRed/trow_bg.png) repeat-x scroll left top #1A1A1A;}
#link_3 a,#link_9 a,#link_32 a,#link_8 a,#link_10 a,#link_29 a,#link_28 a,#link_33 a {background-color: #333;border: none;box-shadow: inset 0 0 0 1px rgba(0, 0, 0, 0.7), 0 0 5px rgba(0, 0, 0, 0.8);color: #fff;text-shadow: 1px 1px #111;}
#link_3 a:hover,#link_9 a:hover,#link_32 a:hover,#link_8 a:hover,#link_10 a:hover,#link_29 a:hover,#link_28 a:hover,#link_33 a:hover {background-color: #a80101;}
}
