css:
@-moz-document url-prefix("https://horriblesubs.info/") {
body {
    background-color: #282828;
    /* ASUNA IS TRASH */
    background-image: none !important;
    background-repeat: no-repeat;
    background-position: right bottom;
    background-size: 365px;
    color: #ffffff;
}
.navbar-brand {
    background-image: none!important;
    color: #F5F7FA !important;
}
.navbar-brand:hover {
    color: /*[[color]]*/
    !important;
}
.navbar.navbar-default {
    background-color: #080808;
    box-shadow: 1px 1px 4px #181818;
}
.navbar-default .navbar-nav > li > a {
    color: #ffffff;
}
.navbar-nav > li:hover > .caret {
    color: /*[[color]]*/;
}
.dropdown-menu > li > a {
    color: #ffffff;
    padding: 0;
}
.dropdown-menu {
    background-color: #383838;
}
.navbar-default .navbar-nav .dropdown-menu > li:hover,
.navbar-default .navbar-nav .dropdown-menu > li:focus,
.dropdown-menu > .active {
    background-color: #484848;
}
.dropdown-menu > .active > a,
.dropdown-menu > .active > a:focus,
.dropdown-menu > .active > a:hover,
.dropdown-menu > .active > .caret,
.dropdown-menu > li > a:focus,
.dropdown-menu > li > a:hover,
.dropdown-menu > li:hover > a,
.dropdown-menu > li:hover > .caret {
    color: /*[[color]]*/;
}
a {
    color: /*[[color]]*/;
}
.navbar-default .navbar-nav > .active > a,
.navbar-default .navbar-nav > .active > .caret,
.navbar-default .navbar-nav > .active > a:hover,
.navbar-default .navbar-nav > .active > a:focus,
.navbar-default .navbar-nav > li > a:hover,
.navbar-default .navbar-nav > li:hover > a,
.navbar-default .navbar-nav > li > a:focus,
.navbar-default .navbar-nav > .open > a,
.navbar-default .navbar-nav > .open > a:hover,
.navbar-default .navbar-nav > .open > a:focus {
    color: /*[[color]]*/;
}
.alert-success {
    color: /*[[color]]*/;
    background-color: #282828;
    border-color: /*[[color]]*/;
}
.post-inner-content {
    background-color: #383838;
    box-shadow: 1px 1px 4px #181818;
}
.series-image img {
    box-shadow: 1px 1px 4px #181818;
}
.featured > .row > div .featured-item .featured-image {
    box-shadow: 1px 1px 4px #181818;
}
.well {
    background-color: #383838;
    box-shadow: 1px 1px 4px #181818;
}
.latest-releases {
    background: #383838
}
.latest-releases .active {
    background: #484848
}
.latest-releases .active a:hover {
    background: #585858!important;
    color: #ffffff !important;
}
.latest-releases a:hover,
.latest-show-more a:hover {
    background: #484848!important;
    color: #ffffff !important;
}
.latest-releases-res .badge,
.rls-label .badge {
    background-color: #282828a8;
}
.rls-info-container a:focus,
.show-more a:focus,
.rls-label:hover,
.entry-title {
    color: /*[[color]]*/;
}
.latest-releases-res:hover .badge:hover,
.rls-label:hover .badge:hover {
    background-color: /*[[color]]*/;
}
.latest-releases a:hover .badge,
.rls-label:hover .badge {
    background-color: #282828a8;
}
.la-badge {
    background-color: #585858;
}
.table-hover > tbody > tr:hover,
.schedule-table > tbody > tr:hover {
    background-color: #585858 !important;
}
.latest-releases a,
.latest-show-more a {
    color: /*[[color]]*/;
}
#secondary .widget a {
    color: #ffffff;
}
a:hover,
a:focus {
    color: #ffffff;
}
#secondary .widget a:hover {
    color: /*[[color]]*/;
}
#secondary .widget a:hover {
    background: transparent;
}
.post-navigation a,
.page-numbers,
.pagination .prev,
.pagination .next {
    border: 1px solid #484848;
    background-color: #383838;
}
.post-navigation a:hover,
.pagination .prev:hover .pagination .next:hover {
    color: #FFF;
    background: #484848;
    border: 1px solid #585858;
}
.entry-meta a {
    color: #ffffff;
}
a .li:hover {
    background-color: #00ff00;
}
.widget ul li {
    border-bottom: 1px solid transparent;
    border-bottom-color: transparent;
}
.table > tbody > tr > td {
    border-bottom: 1px solid transparent;
}
.table-hover > tbody > tr:hover {
    background-color: #484848!important;
}
.form-control {
    color: #ffffff;
    background-color: #282828;
    border-color: #181818
}
.hs-shows {
    border-top: 1px solid transparent;
}
table,
tr,
td {
    border: 1px solid transparent;
}
.latest-releases ul li {
    border-bottom: 1px solid transparent;
}
#disqus_thread {
    background: #383838;
    box-shadow: 1px 1px 4px #181818;
}
.ind-show:hover {
    background-color: #484848!important;
}
.rls-label:hover {
    background: #484848 !important;
}
.rls-info-container {
    border-bottom: 1px solid transparent;
}
.schedule-today {
    background-color: #484848;
}
h4.hrf-title {
    color: #ffffff;
    background-color: #484848;
}
.hrf-content {
    color: #ffffff;
}
h1,
h2,
h3,
h4,
h5,
h6,
.h1,
.h2,
.h3,
.h4,
.h5,
.h6 {
    color: /*[[color]]*/;
}
.hrf-title.close-faq span {
    background-color: #383838;
}
}

@-moz-document url("https://free.timeanddate.com/clock/i4vybhfo/n137/fn17/th1/ta1") {
#c1 {
    background-color: #383838;
}
#t1c,
#t1 {
    color: #ffffff;
}
}

@-moz-document url-prefix("https://disqus.com/embed/comments/?base=default&f=horriblesubs") {
body {
    color: #ffffff;
}
.textarea {
    color: #ffffff;
    background-color: #2a2e2e;
}
.highlighted-post .highlighted .post-content {
    background-color: #383838;
}
.recommend-icon {
    color: /*[[color]]*/;
}
.comment__footer .vote-down,
.comment__footer a,
.comment__header .vote-down,
.comment__header a {
    color: #ffffff;
}
.publisher-anchor-color a {
    color: /*[[color]]*/
    !important;
}
.active .publisher-nav-color::after {
    background: /*[[color]]*/
    !important;
}
.community-name:hover {
    color: /*[[color]]*/;
}
.nav-tab > a {
    color: #ffffff;
}
a:hover {
    color: #ffffff !important;
}
.icon-cog:hover,
.icon-export:hover,
.label:hover {
    color: /*[[color]]*/;
}
.nav-tab > a:hover {
    color: /*[[color]]*/;
}
.unread .notification-icon {
    color: /*[[color]]*/;
}
.dropdown-toggle {
    color: #ffffff!important;
}
.dropdown-toggle:hover {
    color: /*[[color]]*/
    !important;
}
.highlighted > .post-content {
    padding: 12px;
    border-radius: 0px;
}
.tab-conversation.active > a {
    color: #ffffff!important;
}
.thread-likes .label-count {
    color: /*[[color]]*/;
}
.alert.alert--realtime,
.alert.info,
.alert.success,
.dark .alert.alert--realtime,
.dark .alert.info,
.dark .alert.success {
    background: /*[[color]]*/;
}
.post-content.new .indicator,
.post-content.target .indicator {
    background: /*[[color]]*/;
}
.realtime-replies {
    color: #ffffff;
}
.realtime-button .indicator {
    background: /*[[color]]*/;
}
.comment__footer .vote-down:hover,
.comment__footer a:hover,
.comment__header .vote-down:hover,
.comment__header a:hover {
    color: /*[[color]]*/;
}
.post-content .post-menu > li a {
    color: /*[[color]]*/
    !important;
}
h5,
h6 {
    color: /*[[color]]*/;
}
.help-icon {
    border: 2px solid /*[[color]]*/;
}
.help-icon:before {
    color: /*[[color]]*/;
}
.caret {
    border-top: 4px solid /*[[color]]*/;
}
.btn:not(:disabled).active,
.btn:not(:disabled):active {
    background: /*[[color]]*/;
}
.email a:hover,
.install a:hover,
.privacy a:hover {
    color: /*[[color]]*/;
}
.placeholder {
    color: #707070;
}
.postbox section label {
    color: #ffffff;
}
a {
    color: /*[[color]]*/;
}
a:hover {
    color: /*[[color]]*/;
}
.disqus-footer__link {
    color: #ffffff;
}
.textarea-wrapper {
    background: #282828;
    border: 2px solid #000000;
}
.post-actions {
    background: #282828;
    border-top: solid 2px #000000;
}
.input--text {
    background: #282828;
    border: 2px solid #000000;
}
.post-content .post-body spoiler:focus,
.post-content .post-body spoiler:hover {
    background: #383838;
    color: inherit
}
}
