css:
@-moz-document domain(wingsoverscotland.com) {
#page {
	background-color: #282828;
	border: 20px solid #282828;
}
.side2 .gapy {
	background: #333;
}
.post h2 a {
	color: #ccc;
}
h1, h2, h3, h4, h5, h6 {
	color: #ccc;
}
body {
	color: #ccc;
	background-color: #333;
}
a:link, a:visited {
	color: #4794ff;
}
.timr {
	color: #ccc;
}
.stats {
	background: #1a1a1a;
}
.search #s {
    background: #1a1a1a;
	color: #ccc;
	border: 1px solid #666;
}
#footer-out {
	background: #292929;
}
.formin input {
	color: #ccc;
	background: #1a1a1a;
}
.formin textarea {
	color: #ccc;
	background: #1a1a1a;
}
#top {
	background: #333;
}
select#archives-dropdown-3{
	color: #ccc;
    background-color: #444;
    border:  #888;
}
select#cat.postform{
	color: #ccc;
    background-color: #444;
    border:  #888;
}
html body div#page div#sub-page div.side1 ul li#custom-twitter-feeds-widget-2.widget.widget_custom-twitter-feeds-widget div#ctf.ctf.ctf-type-usertimeline.ctf-styles.ctf-narrow.ctf-super-narrow{
    background-color: #333 !important; 
}
html body div#page div#sub-page div.side1 ul li#custom-twitter-feeds-widget-2.widget.widget_custom-twitter-feeds-widget div#ctf.ctf.ctf-type-usertimeline.ctf-styles.ctf-narrow.ctf-super-narrow div.ctf-header.ctf-no-bio{
    background-color: #333 !important; 
}
#ctf .ctf-tweet-actions a.ctf-reply, #ctf .ctf-tweet-actions a.ctf-retweet, #ctf .ctf-tweet-actions a.ctf-like {
	color: #bbb;
}
}
