css:
@-moz-document url-prefix("http://nexxuschat.com/rpg/thekeep/"), url-prefix("http://www.nexxuschat.com/rpg/thekeep/") {
html body.frame {
    background-color: #4e5f7a;
    color: #e8e3dc;
    font-size: 12px;
}
html body.frame div a.user {
    color: #e8e3dc;
}
html body.frame a {
    color: #fff;
    text-decoration: none;
    -o-transition:.5s;
    -ms-transition:.5s;
    -moz-transition:.5s;
    -webkit-transition:.5s;
    transition:.5s;
}
html body.frame a:hover {
    color: #efb345;
}
body > center:nth-child(1) > a:nth-child(1) > font:nth-child(1) {
    display: none;
    visibility: hidden;
}
body > center:nth-child(1) > a:nth-child(4) > img:nth-child(1), html body.frame .parent a img {
    display: none;
    visibility: hidden;
}
html body.frame div a.user img {
    width: 12px;
    height: 12px;
    opacity: .5;
    transition: opacity .5s ease-out;
    -moz-transition: opacity .5s ease-out;
    -webkit-transition: opacity .5s ease-out;
    -o-transition: opacity .5s ease-out;
}
html body.frame div a.user img:hover {
    opacity: 1;
}
html body.frame input {
    background-color: #161b23;
    border: 1px solid #475770;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    padding: 5px;
    margin-bottom: -10px;
}
body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(1) > input:nth-child(19) {
    padding-left: 8px;
    padding-right: 8px;
    font-weight: bold;
    text-transform: uppercase;
    cursor: pointer;
    -o-transition:.5s;
    -ms-transition:.5s;
    -moz-transition:.5s;
    -webkit-transition:.5s;
    transition:.5s;
}
body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(1) > input:nth-child(19):hover {
	background-color: #443314;
}
body > table:nth-child(1) {
    width: 100%;
}
body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > form:nth-child(1) > input:nth-child(16) {
    width: 95%;
}
body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(2) {
    float: right;
}
body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) {
    float: right;
    margin-right: 25px;
    padding-left: 10px;
}
.parent, body > b:nth-child(3) {
    font-size: 13px;
}
.small {
    color: #e8e3dc;
    font-size: xx-small;
}
.time {
    color: #858585;
    -o-transition:.5s;
    -ms-transition:.5s;
    -moz-transition:.5s;
    -webkit-transition:.5s;
    transition:.5s;    
    font-size: xx-small;
}
.time:hover {
	color: #000;
}
}
