css:
/* i really want this to be global */

#wrap {
    color: white !important;
}

#wrap h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
    color: white;
    font-family: Gilroy, Open Sans, sans-serif;
}

.text-muted {
    color: #888888;
}

.main-view {
    background-color: #212121;
}

.breadcrumb-currency-picker > ul {
    background-color: #303030;
}

.big-info p.small-title {
    color: white;
}

table tbody tr:nth-of-type(odd) {
    background: #303030 !important;
}

.table-hover > tbody > tr:hover {
    background-color: black !important;
}

.table-hover>tbody>tr:hover>td, .table-hover>tbody>tr:hover>th {
    background-color: black !important;
}

qrcode {
    background-color: white !important;
}

.nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus {
    color: white;
    background-color: #212121;
    border: 1px solid #303030;
}

.nav-tabs {
    border-bottom: 1px solid #303030;
}

.nav-tabs>li>a {
    color: #2096ba;
}

.btn-pagination-inactive {
    color: #D9DCE3;
    background-color: #303030;
}

.btn-next, .btn-prev {
    color: #2475b9;
    border: solid 1px #2475b9;
    background-color: #303030;
}

.form-control {
    background-color: #303030;
}

.btn-default {
    color: white;
    background-color: #303030;
    border-color: #212121;
}

div.platforms div.platform .details-wrapper, div.versions div.version .details-wrapper, div.heights div.height .details-wrapper {
    background: #303030;
}

div.platforms div.platform .counter, div.versions div.version .counter, div.heights div.height .counter {
    background-color: #424242;
}
