    background-image: url(https://upload.wikimedia.org/wikipedia/commons/thumb/2/21/Solid_black.svg/2000px-Solid_black.svg.png);
    background-position: top center;
    background-attachment: fixed;
    cursor: pointer;   }

#navBar 
{    background-color: #636262;   }

element.style
{  border-bottom: 1px solid #fff;  }

.tvshow > .right > ul.active
{   background-color: #333639;
    display: block;   }

.tvshow
{  background-color: #333639;}



.tvshow > .left > ul > li.active > a 
{   background-color: #ffffffe8;
    color: #000;
    text-shadow: -1px 1px 1px #00000000;   }

.tvshow > .right > ul > li > a 
{  color: #ffffff; }




.textcontent 
{  background-color: #333639  }

#banner {    background-color: #333639; }

#page
 {background-color: #333639  }

#menu
{ background-color: #636262;   }

#menu li:hover > a, #menu li.active > a 
{background-color: #636262;}


#noticebar p.center
{   background-color: #999;
    color: #000; }
.thumbs li {
    margin: 0 0 10px 10px;
    padding: 10px;
    width: 164px;
    height: 145px;
    background: #00000078;
    float: left;
    position: relative;
}
.tvshow > .right > ul > li > a 
{  color: #ffffff; }
.thumbs li .title a
{ color: #ffffff;}


.thumbs li:hover, .thumbs li.active {
background : #8e8e8e;
}
.pageselect li a:hover, .pageselect li.active a {
background-color : #373636;
text-decoration : none;
}
.pageselect li a {
    background-color: #5b5b5b;
    border: 1px solid #fff;
    color: #FFF;
    display: inline-block;
    font-size: 14px;
    height: 26px;
    margin: 0 1px 0 0;
    line-height: 26px;
    text-align: center;
    text-shadow: -1px 1px 1px #000000;
    width: 26px;
}
.tabbed a {
    background-color: #000000;
    border-radius: 20px 5px 0px 0px;
    color: #FFF;
    display: block;
    margin: 0 0 0 3px;
    padding: 5px 10px;
    text-shadow: -1px 1px 1px #000000;
}
.tabbed li.active a {
    background-color: #c5c5c5;
}
.tabbed {
    border-bottom: 2px solid #000;
    list-style-type: none;
    margin: 10px 0 0;
    padding: 0 5px;
}
.datechanger {
    background-color: #c5c5c5;
    margin: 0 0 10px;
    padding: 0 0 0 10px;
}
.episodes td {
    background-color: #9c9999;
    padding: 10px;
}
.episodes td:hover {
background-color : #5d5850;
}
}
