css:
@-moz-document url-prefix("https://summonerswar-fanforum.de/"), url-prefix("https://summonerswar-fanforum.de/index.php") {
/* Main Styles */
 body {
    background-color: #333;
}
.pagination a {
    border: none;
    border: 1px solid #666;
    border-radius: 3px;
    background-color: #444;
}
.pagination .pagination_current {
    color: #bbb;
}
.pagination a:hover {
    border-color: #90caf9;
    border-radius: 3px;
    background-color: #2196f3;
}
a.button:link, a.button:hover, a.button:visited, a.button:active {
    background: none;
    border: none;
    border: 1px solid #666;
    border-radius: 3px;
    background-color: #444;
}
a.button:hover {
    border-color: #90caf9;
    background-color: #2196f3;
}
a:visited, a:link {
    color: #fff;
}
html, body, p, code:before, table {
    color: #e0e0e0;
}
.sceditor-container {
    background: none !important;
    background-color: #333 !important;
}
.sceditor-container iframe, .sceditor-container textarea {
    background-color: #333 !important;
}
div.sceditor-toolbar {
    border: none !important;
    background: none !important;
    background-color: #ddd !important;
}
#panel .lower a:link, #panel .lower a:visited, #panel .lower a:hover, #panel .lower a:active {
    color: #444;
}
button, input.button, input.textbox, input.invalid_field, input.valid_field, select, textarea, .editor_control_bar, blockquote, .codeblock, fieldset, .pm_alert, .red_alert, .popup_menu, .postbit_buttons > a, a.button {
    border-radius: 3px;
}
#header ul.menu li {
    color: #999;
}
#container {
    position: absolute;
    top: 0;
    width: 100%;
    color: #999;
}
#container {
    padding: 0;
}
body > form {
    position: relative;
    z-index: 5;
}
#logo, #content {
    background: none;
}
#logo {
    background-image: url(https://stmed.net/sites/default/files/summoners-war%3A-sky-arena-hd-wallpapers-33714-2441262.jpg);
    background-position: center;
    background-repeat: repeat-x;
    background-attachment: fixed;
    border: none;
    padding-top: 50px;
}
#logo ul.top_links {
    margin-top: 3em;
    float: right;
}
#panel > div.lower {
    background-color: #ddd;
    border: none;
    border-top: 1px solid #eee;
}
#Ann_2, #Ann_11 {
    display: none;
}
.tborder {
    padding: 0;
    border: none;
    color: #bbb;
}
.thead {
    background: none;
    background-color: #2196f3;
    border: none;
    border-bottom: solid 1px #03a9f4;
}
.tcat {
    background: none;
    background-color: #616161;
    border: none;
    border-bottom: solid 1px #333;
}
.trow1 {
    background-color: #444;
    border: 0.5px solid;
    border-color: #333;
}
.trow2 {
    background-color: #444;
    border: 0.5px solid;
    border-color: #333;
}
#cat_86_e > tr:nth-child(3) > td:nth-child(2) > strong > a > font {
    color: #ff9800;
}
#shoutbox {
    border: none;
    margin-bottom: 20px;
}
#shoutbox > div.head {
    background-color: #ddd;
    border: none;
    border-top: solid 1px #eee;
    border-bottom: solid 1px #999;
    border-top-left-radius: 6px;
    border-top-right-radius: 6px;
    color: #444;
}
#shoutbox > div.head > p > a {
    color: #444;
}
#shoutbox > div.body > div.panel {
    border: none;
    border-bottom: solid 1px #333;
}
#shoutbox .entry > div {
    background-color: #444;
    border-bottom: solid 1px #333;
}
#shoutbox .text {
    color: #bbb;
}
td.tfoot {
    background-color: #ddd;
    border: none;
    border-top: solid 1px #eee;
    color: #444;
}
#footer .upper {
    background-color: #ddd;
    border: none;
    border-top: solid 1px #eee;
    color: #444;
}
#footer a:link, #footer a:visited, #footer a:hover, #footer a:active {
    color: #444;
}
/* Member Overview */
 body {
}
table {
    color: #bbb;
}
.navigation .active {
    color: #bbb;
}
.navigation {
    color: #bbb;
}
.largetext {
    color: #fff;
}
.online {
    color: #4CAF50;
}
}

@-moz-document url-prefix("https://summonerswar-fanforum.de/showthread.php?"), url-prefix("https://summonerswar-fanforum.de/private.php?action=read"), url-prefix("https://summonerswar-fanforum.de/newreply.php"), url-prefix("https://summonerswar-fanforum.de/newthread.php") {
/* Posts */
 body {
}
blockquote {
    border: none;
    border: 1px solid #9e9e9e;
    background-color: #616161;
}
blockquote cite {
    border: none;
    border-bottom: 1px solid #9e9e9e;
}
blockquote cite span {
    color: #bdbdbd;
}
#posts_container {
    background-color: #444;
}
.post .post_author div.author_avatar img {
    padding: 0;
}
.post .post_author div.author_avatar {
    margin: 5px;
}
.post .post_author {
    background-color: #616161;
    border: none;
    color: #ffeb3b;
}
.post_author {
    background-color: #444;
}
.post .post_author div.author_statistics {
    color: #e0e0e0;
}
.reputation_positive {
    color: #90caf9;
}
.post .post_head span.post_date {
    color: #bdbdbd;
}
.post .post_head span.edited_post a {
    color: #90caf9;
}
.post .post_head {
    background-color: #444;
    border-bottom: 1px dotted #666;
}
.post_content .signature {
    border-top: 1px dotted #666;
}
.post_content {
    color: #e0e0e0;
}
.post_body {
    background-color: #444;
}
.post_controls {
    background-color: #ddd;
    border-top: 1px solid #eee;
}
.thx_list {
    color: #444;
}
#quickreply_e > tr:nth-child(1) > td:nth-child(2) > div:nth-child(1) {
    width: auto !important;
    margin: auto;
}
textarea#message {
    width: 99% !important;
}
.editor_control_bar {
    background: none;
    border: 1px solid #bbb;
    width: auto !important;
}
}

@-moz-document url-prefix("https://summonerswar-fanforum.de/member.php") {
/* Member Overview */
 body {
}
table {
    color: #bbb;
}
.navigation .active {
    color: #bbb;
}
.navigation {
    color: #bbb;
}
.largetext {
    color: #fff;
}
.online {
    color: #4CAF50;
}
}

@-moz-document url("https://summonerswar-fanforum.de/private.php") {
.tborder tbody tr:last-child td:last-child, .tborder tbody tr:last-child td:last-child {
    border-radius: 0;
}
}

@-moz-document url("https://summonerswar-fanforum.de/search.php") {
.tborder tbody tr:last-child td:last-child, .tborder tbody tr:last-child td:last-child {
    border-radius: 0;
}
.tcat {
    background: none;
    background-color: #616161;
    border: none;
}
}
