css:
/* i really want this to be global */
.module--secondary, .module-bg, .page-footer, .page-head__bar {
    position: relative;
    color: #fff;
    background-color: #2C3E50;
}
.tebexLogin {
    font-family: "Open Sans", "Helvetica Neue", Helvetica, Arial, sans-serif;
    height: 52px;
    background: #48ADDE;
    color: #ffffff;
    display: block !important;
    width: 100%;
    opacity: 1 !important;
    visibility: visible !important;
    position: absolute;
    top: 0px;
    left: 0px;
    font-size: 90%;
    z-index: 1337;
}
.main-nav__list li a:focus, .main-nav__list li a:hover, .main-nav__list li.current>a {
    color: #fff;
    background-color: #405c77;
}
.mfp-ajax-holder .mfp-content, .module {
    position: relative;
    padding: 24px;
    border-radius: 2px;
    background-color: #173853;
    box-shadow: 0 2px 10px 0 rgba(0,0,0,.15), 0 2px 5px 0 rgba(0,0,0,.25);
}
.module__heading, h2 {
    color: #48adde;
}
.module--secondary h1, .module--secondary h2, .module--secondary h3, .module--secondary h4, .module--secondary h5, .module--secondary h6 {
    color: #48adde;
}
span {
color: #fff;
}
.mfp-ajax-holder .module__content>.mfp-content:last-child, .module__content>.flag:last-child, .module__content>.form__group:last-child, .module__content>.island:last-child, .module__content>.list:last-child, .module__content>.margin:last-child, .module__content>.media:last-child, .module__content>.module:last-child, .module__content>.nav:last-child, .module__content>.package:last-child, .module__content>.package__desc:last-child, .module__content>.package__image--detail:last-child, .module__content>.package__image:last-child, .module__content>.page-head:last-child, .module__content>.page-head__bar:last-child, .module__content>.progress:last-child, .module__content>.table:last-child, .module__content>h1:last-child, .module__content>h2:last-child, .module__content>h3:last-child, .module__content>h4:last-child, .module__content>h5:last-child, .module__content>h6:last-child, .module__content>p:last-child {
    margin-bottom: 0;
    color: #48adde;
}
.package__name, strong {
    font-weight: 700;
    color: #fff;
}
element.style {
    color: #fff;
}
.ign, .package__name, h1, h2, h3, h4, h5, h6 {
    font-family: "Open Sans Condensed",Helvetica,sans-serif;
    color: #fff;
}
.price {
    color: #48adde;
}
.extra {
   color: #fff;
}
.fa {
    display: inline-block;
    font-family: FontAwesome;
    font-style: normal;
    font-weight: normal;
    line-height: 1;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    color: #48adde;
}
.button--accept {
    background-color: #12820c;
}
.button--accept:focus, .button--accept:hover {
    color: #0e2334;
    background-color: #2bb524;
    box-shadow: inset 0 0 0 2px #2bb524;
}

.mfp-ajax-holder .mfp-content .module__heading, .mfp-ajax-holder .mfp-content h2, .module .module__heading, .module h2 {
    margin-top: 48px;
    margin-bottom: 24px;
    padding-bottom: 12px;
    border-bottom: 4px solid #48adde;
}
.ign, .package__name, h1, h2, h3, h4, h5, h6 {
    font-family: "Open Sans Condensed",Helvetica,sans-serif;
    color: #48adde;
}
.flag, .form__group, .island, .list, .margin, .media, .mfp-ajax-holder .mfp-content, .module, .nav, .package, .package__desc, .package__image, .package__image--detail, .page-head, .page-head__bar, .progress, .table, h1, h2, h3, h4, h5, h6, p {
    margin-top: 0;
    margin-bottom: 24px;
    color: #fff;
}
