css:
@-moz-document domain("www.thebettermeta.com") {
body {
    background-color: #10171E;
    color: #dfdfdf;
}

.footer{
    background-color: #15202B;
}

.site-name a, .nav-container a, .site-name a:visited, .nav-container a:visited{
    color: #c3d2ff;
}

a, .site-name a:hover{
    text-decoration: none;
}

a{
    color: #1DA1F2;
}

a:hover {
    text-decoration: none;
}

dd{
    color: #dfdfdf;
    font-style: italic;
}

em{
    background-color: #132431;
}

.triple {
    background-image: linear-gradient(transparent, #15202B);
}


}

@-moz-document url-prefix("https://www.thebettermeta.com/charts/"), url-prefix("https://www.thebettermeta.com/posts/") {
.chart-body{
    border: 0;
}

em{
    background-color: transparent !important;
}

.chart-tabs{
    background-color: #131c26 !important;
    border: 0 !important;
}

.tablink {
    background-color: #1B3448 !important;
    border: 0 !important;
    margin: 5px;
    padding: 12px 14px !important;
    color: #fff;
}

.chart-tabs button.active {
    background-color: orange !important;
    color: #000;
}

.chart-tabs button:hover:not(.active) {
    background-color: #234f5f !important;
}

.chart-body{
    background-color: #15202B !important;
}

rect[fill='#ffffff']{
    fill: #15202B !important;
}

rect[fill='#cccccc']{
    fill: #3d4f61 !important;
}

text{
    fill: #93b9c7 !important;
}

text[text-anchor='start']{
    fill: #fff !important;
}

path[fill='#ffffff']{
    fill: #3d4f61 !important;
    stroke-width: 0 !important;
}

[fill='#661100']{
    fill: #a71c00 !important;
}

th{
    background-color: #131C26 !important;
}

th.active{
    background-color: #1B3448 !important;
}

.legend-row-6 {
    background-color: #a71c00;
}
}

@-moz-document url("https://www.thebettermeta.com/charts/role_splits/") {
rect[fill='#252525']{
    stroke-width: 1px !important;
    stroke: white !important;
}

rect[fill='#ebebeb']{
    fill: transparent !important;
}

rect[fill='#cccccc'] {
    fill: #5b7690 !important;
}
}

@-moz-document url("https://www.thebettermeta.com/charts/legendaries/") {
div::-webkit-scrollbar {
    width: 1em;
}
 
div::-webkit-scrollbar-track {
    background-color: #1b1d23;
}
 
div::-webkit-scrollbar-thumb {
    background-color: #1883c5;
    outline: 1px solid #0080ff;
    border-radius: 3px;
}

.google-visualization-table-tr-even {
    background-color: #353535 !important;
    color: #fff !important;
}
.google-visualization-table-tr-odd {
    background-color: #1b1b1b !important;
    color: #dbdbdb  !important;
}
.google-visualization-table-tr-odd.google-visualization-table-tr-over {
    color: #ffce77  !important;
}
.google-visualization-table-tr-even.google-visualization-table-tr-over {
    color: #ffce77  !important;
}
.google-visualization-table-tr-even a {
    color: #00f7ff  !important;
}
.google-visualization-table-tr-odd a {
    color: #00ced4  !important;
}
.google-visualization-table-td {
    border: none !important;
}

.google-visualization-table-th {
    background-color: #2f3343;
    background-image: none !important;
}

.chart-wrapper {
    width: 60% !important;
}
}

@-moz-document url("https://www.thebettermeta.com/charts/winrate_by_map/") {
circle {
    stroke: white;
    stroke-width: 0.5px;
}


}

@-moz-document url("https://www.thebettermeta.com/charts/champion_rating_overview/") {
rect[fill='#333333']{
    fill: #bcbcbc;
}

.thinbox{
    border: 0;
}
}

@-moz-document url("https://www.thebettermeta.com/charts/role_composition/") {
.thinbox{
    border: 0;
}

.thinbox label{
    padding-left: 15px;
}

path[fill='#008000'], path[fill='#ff0000'], path[fill='#ffff00'], path[fill='#0000ff']{
    stroke: #fff !important;
}

rect:not([fill='#3366cc']){
    fill-opacity: 0.5;
}
}
