css:
@-moz-document domain("tools.pdf24.org") {
body {
  background-color: #222;
  color: #aaa;
}

a:hover:not(.btn), .link:hover {
  color: #007ee5; /* from html.blueTheme > --theme-color-normal */
}

/* Sub-header 'Free No limits Secure' */
#tool .noLimits {
  background-color: #282828;
  border-color: #333;
}

/* Top Icon */
#topBar .logo {
  background-color: #222;
}

#topBar .logo img {
  opacity: 0.7;
}

/*** 'Drop PDF files here or click to select files' ***/

/** Before processing **/

.dropzone {
  background-color: #282828;
}

/* Uploading file box */
.dropzone .dz-preview.dz-file-preview .dz-image {
  background: #333;
}

.dropzone .dz-preview .dz-image {
  border-color: #333;
}

.dropzone .dz-preview .dz-details .dz-filename span, .dropzone .dz-preview .dz-details .dz-size span {
  background-color: #333;
}

.dropzone .dz-preview .dz-details .dz-filename:hover span {
  background-color: #333;
  border-color: #666;
}

.dropzone .dz-preview .dz-details {
  color: #aaa;
}

/* FileSign on left-bottom corner */
.dropzone .fileSecuritySign path[fill="#FFFFFF"] {
  fill: #282828;
}

/* Dropbox, Google Drive icons on bottom-right corner */
.dropzone .importTools svg:hover {
  background-color: #282828;
}

.dropzone .importTools path[fill="#007EE5"]:hover {
  fill: #E48922;
}

.dropzone .importTools g[fill="#007EE5"]:hover {
  fill: #E48922;
}


/* Upload Parameters */
#params {
  background: #282828;
  border-color: #666;
}

select {
  border-color: #666;
}

/* 'Start ____' button */
.btn {
  background-color: #222;
  border-color: #333;
  color: #aaa;
  text-shadow: -1px 0px #666, 1px 0px #666, 0px -1px #666, 0px 1px #666;
}

/** While processing **/

#workerZone, .workerZone {
  background-color: #282828;
}

.workerZone .info {
  background-color: #333;
  color: #ccc;
}

.workerZone .info .key {
  color: #aaa;
}

.workerZone .info .value {
  color: #6868ff; /* navy; */
}

.dropzone .dz-preview .dz-progress {
  background-color: rgba(40,40,40,.9);
}

.dropzone .dz-preview .dz-progress .dz-upload {
  background: linear-gradient(to bottom,#999,#444);
}

/**** 'Information' section ****/

#tool #info .cell::before {
  filter: invert(70%);
}

.section.even, .toolSelect .tile:hover {
  background-color: #282828 !important;
}

.sectionsCollapsed .section.expand .collapseHandle {
  background-color: #ececec;
  filter: invert(0.9);
}

.sectionsCollapsed .section:not(.expand):hover {
  color: #007ee5; /* from html.blueTheme > --theme-color-normal */
}

/**** 'What others are saying' section ****/

blockquote {
  background-color: #222;
  box-shadow: 2px 2px 15px #333;
}


/**** 'Please rate our tool' section ****/

#rate textArea {
  box-shadow: 0px 0px 5px #282828;
}

/**** 'Please share this page with your friends' section ****/

#share img {
  filter: invert(85%);
}

/**** 'More great tools' section ****/

.toolSelect .tile {
  background-color: #282828;
  border-color: #333 !important;
}

.toolSelect .icon img {
  opacity: 0.7;
}

.toolSelect a:hover {
  color: #7fbef2;; /* from html.blueTheme > --theme-color-border */
}


/**** 'Alternative: PDF24 Creator' section ****/

.lazyLoad {
  opacity: 0.5;
}

/**** Footer *****/
#footer {
  background-color: #222;
  border-top-color: #333;
}

a, a:visited, .link {
  color: #aaa;
}

/* Social icons */
#footerSocial {
  filter: invert(85%);
}

/* Language selection dropdown */
#footerLangSwitch ul li {
  background-color: #222;
}

#footerLangSwitch ul {
  border-color: #333;
}

#footerLangSwitch li:hover {
  background-color: #282828;
}

#footerLangSwitch ul.clicked li.selected a {
  color: #007ee5; /* from html.blueTheme > --theme-color-normal */
}

}

@-moz-document regexp("https:\\/\\/filetools[0-9].pdf24.org\\/state.php.*") {
body {
  color: #aaa;
}

#social {
  background-color: #222;
}

/* Google + Share icon */
img[src="/img/gplus_share.png"] {
  filter: invert(70%);
}

img[src="/img/twitter_share.png"] {
  opacity: 0.7;
}

/* Waiting Spinner */
#animicon {
  filter: invert(70%);
}


/* 'Download', 'Mail', 'Fax' buttons */
.button {
  color: #ccc;
}

/* Error Smiley :( */
#errorIcon img {
  filter: invert(50%);
}

/* 'Show more tools'-hover-msg button, after 'PDF24 has processed your job.' */
#tools .tool.moreToolsTool {
  background-color: #333;
}

#tools .tool.moreToolsTool i {
  color: #aaa !important;
}

}
