#page-header-wrapper
{
width: 100%;
 /*background-color: #5da252;*/
}

body {
background-color: #eaeaea;
}

#page-header-wrapper {

	background: linear-gradient(-45deg, #EE7752, #E73C7E, #23A6D5, #23D5AB);
	-webkit-animation: Gradient 15s ease infinite;
	-moz-animation: Gradient 15s ease infinite;
	/*animation: Gradient 15s ease infinite;*/
-webkit-animation: color 5s ease infinite;
-moz-animation: color 5s ease infinite ;
animation: color 5s ease infinite ;
}

@-webkit-keyframes Gradient {
	0% {
		background-position: 0% 50%
	}
	50% {
		background-position: 100% 50%
	}
	100% {
		background-position: 0% 50%
	}
}

@-moz-keyframes Gradient {
	0% {
		background-position: 0% 50%
	}
	50% {
		background-position: 100% 50%
	}
	100% {
		background-position: 0% 50%
	}
}

@keyframes Gradient {
	0% {
		background-position: 0% 50%
	}
	50% {
		background-position: 100% 50%
	}
	100% {
		background-position: 0% 50%
	}
}

a, a:visited, .tabtree .tabrow0 li a, .dropdown-menu.usermen > li > a
{
  color: #225927;
}

 .content a {
   color: #22AA44
}

#logocontainer {
-webkit-animation:floating 5s ease infinite;
-moz-animation: floating 5s ease infinite ;
animation: floating 5s ease infinite ;
}

#logo {
-webkit-animation: turn 0.5s linear infinite;
-moz-animation: turn  0.5s linear infinite;
animation: turn 0.5s linear infinite;
}

@keyframes color {
	0% {background-position: left top;}
50% {background-position: right top;}
	100% {background-position: left top;}
}

@keyframes turn {
    from { transform: rotate3d(0, 1, 0, 0deg);     }
    50% { transform: rotate3d(0, 1, 0, 180deg);     }
    to { transform: rotate3d(0, 1, 0, 360deg);     }
}
@keyframes bounce {
    from { transform: translate(0,  20px) }
    50%  { transform: translate(0,  -40px)}
    to   { transform: translate(0,  20px)}    
}

@keyframes floating {
    from { transform: translate(0,  -30px) scale(1); }
    50%  { transform: translate(0,  20px) scale(1); }
    to   { transform: translate(0,  -30px) scale(1); }    
}

@-moz-keyframes spin { 100% { -moz-transform: rotate(360deg); } }
@-webkit-keyframes spin { 100% { -webkit-transform: rotate(-360deg); } }
@keyframes spin { 100% { -webkit-transform: rotate(360deg); transform:rotate(360deg); } }


