@-moz-document domain("25thchestnutnoveltranslations.wordpress.com") {
/*/////////////////*/
/*Colors used in the style are defined here*/

:root {
    /*Background*/
    --bgmain: #eee;
    --bgbanner: #333; /*Site header*/
    --bgtable: #f2f2f2;
    
    /*Text*/
    --site: #ddd;
    --header: #222;
    --title: #ddd;
    --body: #222;
    
    /*Misc*/
    --shadow: #444; /*Drop shadow*/
    --border: #ccc;
}
}

@-moz-document domain("25thchestnutnoveltranslations.wordpress.com") {
/*Remove Wordpress ad bwahahaha ------------------------------------------*/
html {
    margin-top: 0!important;
}

#wpadminbar.marketing-bar {
    display:none!important;
}

/*/////////////////*/
/*Set color --------------------------------------------------------------*/
body, button, input, select, textarea,.main-navigation a {	
    color: var(--body);}

body.custom-background, .site-header {	
    background-color: var(--bgmain);}

.header-wrapper {
    background-color: var(--bgbanner);
}

.site-title a, .site-branding-text {
    color: var(--site);
}
table tbody {
    background-color: var(--bgtable)!important;
}

/*/////////////////*/
/*Set site header to always show -----------------------------------------*/
/*!!!This is a hack. There are probably ways to set it server-side!!!-----*/
.site-header {
    position: fixed;
    left:0;
    right:0;
    z-index:999;
}

.site-content { /*Hack content space to give room for fixed header*/
    margin-top: 250px;
}

/*/////////////////*/
/*Center navigation menu -------------------------------------------------*/
.main-navigation {
    justify-content: center!important;
    padding-top: 5px;
}

/*/////////////////*/
/*Tweak site header ------------------------------------------------------*/
.header-wrapper {
    padding: 35px 0 25px 0;
    /*Add drop shadow under header*/
    box-shadow: 3px 3px 5px 0 var(--shadow);
    
    /*Span header to whole screen*/
    width: 100%!important; 
    max-width: 100%!important;    
    margin: 0!important; /*Remove margin to allow span*/
}

/*Set margin for site header text*/
.site-title a, .site-branding-text {
    margin: 0px 20px 0px 20px;
}

/*Change header font to Lato*/
.site-title a {
    font-family: Lato!important;
    font-weight: 900;
    text-transform: uppercase;
}
.site-branding-text {
    font-family: Lato!important;
    font-size: 2.3rem;
    font-weight: 300;
    letter-spacing: 0.125em;
}

/*/////////////////*/
/*Increase size of selected menu -----------------------------------------*/
.main-navigation li.current-menu-item > a {
    box-shadow: 2px 2px 4px 0 var(--shadow);
	font-size: 2.5rem!important;
    position: relative;
    bottom: 8px;
}

/*/////////////////*/
/*Change padding for entry header ----------------------------------------*/
.entry-header {
    padding: 0 0 30px 0;
}

/*/////////////////*/
/*Change style for entry header ------------------------------------------*/
h1.entry-title {
    font-family: Lato;
    font-weight: 900;
    font-size: 3rem!important;
    text-align: center;
    border-bottom: 2px solid var(--border);
    padding: 20px 0 20px 0;
    width: 800px;
}

/*/////////////////*/
/*Hide entry headers for redundant menu items ---------------------------------*/

/*Latest Release*/      .post-551 .entry-title,
/*Table of Contents*/   .post-21 .entry-title,
/*Release Schedule*/    .post-492 .entry-title,
/*Patch Notes*/         .post-560 .entry-title {
    display:none;
}


/*/////////////////*/
/*Change style for entry content -----------------------------------------*/
.entry-content {
    font-size: 1.8rem;
    line-height: 1.5;
    width: 800px;
}

/*Change style for chapter names*/
.has-large-font-size {
	font-size: 2rem;
    font-weight: 600;
}

/*Align table contents to top*/
td {
	vertical-align: top;
    line-height: 1.5;
}

/*Add border to tables*/
tr {
	border: 1px solid var(--border);
}

/*/////////////////*/
/*Blanket font change ----------------------------------------------------*/
.wf-active .header-search .s, .wf-active .entry-meta, .wf-active .site-footer, .wf-active .main-navigation a, .wf-active .entry-footer, .wf-active .post-navigation a span, .wf-active .comment-actions, .wf-active .comment-navigation, .wf-active #wp-calendar tfoot a, .wf-active form, .wf-active input, .wf-active button, .wf-active .button, .wf-active input[type="button"], .wf-active input[type="reset"], .wf-active input[type="submit"], .wf-active #infinite-handle span, .post-navigation a {
    letter-spacing: 0.5px;
	font-family: Lato!important;
}
.wf-active body, .wf-active button, .wf-active input, .wf-active select, .wf-active textarea {
	font-family: Open Sans;
}
}

