css:
@-moz-document domain('x.sankuai.com'), domain('neixin.cn') {
	#preview-web-global-watermark {
		background: none;
	}
    body {
        font-family:'Roboto Mono', 'Monaco', 'Noto Sans CJK SC', 'PingFang SC' monospace;
        background: #3F5263;
        background-image: linear-gradient(-135deg, #ABABAB 0%, #969696 19%, #7E7E7E 100%);
    }
    #main {
        position: relative;
        width: 80vw;
        height: 90vh;
        overflow: hidden;
        max-width: 1000px;
        min-width: 800px;
        max-height: 1000px;
        min-height: 400px;
        border: 1px solid rgba(0, 0, 0, 0.3);
        border-radius: 4px;
        margin: 5vh auto;
        box-shadow: rgba(0, 0, 0, 0.3) 0 10px 100px;
    }
    .comp-chat-session-list {
        font-family:'Radikal', 'Avenir Next', 'Noto Sans CJK SC', 'PingFang SC', sans-serif;
    }
    .titlebar .title {
        font-family:'Radikal', 'Avenir Next', 'Noto Sans CJK SC', 'PingFang SC', sans-serif;
        font-size: 18px;
        font-weight: 600;
    }
    .titlebar .title .wrapper-desc {
        line-height: 18px;
        font-weight: 100;
        line-height: 26px;
        display: inline;
        margin-left: 0.5em;
    }
    .comp-noticeBanner {
        position: relative;
        width: 60%;
        margin: 1vh -30%;
    }
    .text-input .ctn-send-msg-btn {
        display: none;
    }
}
