css:
@-moz-document domain("115.com") {
.navigation-ceiling li:not(:first-child),.container-ceiling .btn-ceiling,.ceiling-link-temp,.container-core .sub-core,#js_recent_view_btn{display:none !important;}
.top-vflow .left-tvf a:nth-child(5),
.top-vflow .left-tvf a:nth-child(6){display:none;}
.top-vflow .right-tvf div:nth-child(3){display:none;}
.search-ceiling{left:300px;}
.list-contents .file-opr a[menu='edit_file_label'],
.list-contents .file-opr a[menu='edit'],
.list-contents .file-opr a[menu='star'],
.list-contents .file-opr a[menu='public_share'],
.list-contents .file-opr a[menu_btn='more']{display:none;}
.list-contents .file-opr a[menu='delete']:after{display:none;}
.list-contents .file-opr{left:auto;right:30%;}
.container-core .main-core{left:0;}
.list-operate li[menu='edit_file_label'],
.list-operate li[menu='edit'],
.list-operate li[menu='star'],
.list-operate li[menu='public_share'],
.list-operate li[menu='set_copy'],
.list-operate li[menu='copy_list'],
.list-operate li[menu='hide_file'],
.list-operate li[menu='cover'],
.list-operate li[menu='same'],
.list-operate li[menu='export_dir'],
.list-operate li[menu='attribute'],
.list-operate li[menu='trans_to_album']{display:none;}
