/*apply rule*/
{display: none;}

/* less clothes size:. If you don't need it, remove it: */
/* if too small for you, change it to 70px/71px or 80/81px etc, original size: 100px/102px */
.appearance-item {
    height: 80px;
    width: 81px;
    text-align: center;
}

 
/* items height: */
.appearance-items-list {
      max-height: 425px;
}

 /* items width: */
#appearance-items {
    width: 670px;
    margin-left: -20px;
    margin-top: 35px;
}
#appearance-category-nav { 
    width: 648px !important;
}

/* content size fix */
#content {
    padding-bottom: 82px;
    width: 1120px;
    margin: auto;
}

/* control buttons*/
.appearance-items-info {
    width: 635px !important;
    height: 80px !important;
}
 
}
